const hisnulMuslimData = {
  de: {
    title: "Hisnul Muslim",
    description: "Eine Festung für den Muslim – eine Sammlung authentischer Bittgebete aus dem Koran und der Sunnah.",
    backToFeatures: "Zurück zu den Funktionen",
    searchPlaceholder: "Suche nach einem Bittgebet...",
    toastFavoriteSaved: "Favorit gespeichert!",
    toastErrorSaving: "Fehler beim Speichern.",
    loginToSave: "Anmelden zum Speichern",
    memorize: "Lernen",
    chapters: [
        { id: 1, title: 'Beim Aufwachen', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي أَحْيَانَا بَعْدَ مَا أَمَاتَنَا وَإِلَيْهِ النُّشُورُ', transliteration: 'Alhamdu lillahil-ladhi ahyana ba`da ma amatana wa ilayhin-nushur.', translation: 'Alles Lob gebührt Allah, der uns wiederbelebt hat, nachdem Er uns hat sterben lassen, und zu Ihm ist die Auferstehung.' }] },
        { id: 2, title: 'Beim Anziehen eines Kleidungsstücks', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي كَسَانِي هَذَا (الثَّوْبَ) وَرَزَقَنِيهِ مِنْ غَيْرِ حَوْلٍ مِنِّي وَلَا قُوَّةٍ', transliteration: 'Alhamdu lillahil-ladhi kasani hadha (ath-thawba) wa razaqanihi min ghayri hawlin minni wa la quwwatin.', translation: 'Alles Lob gebührt Allah, der mich mit diesem (Kleidungsstück) bekleidet und es mir ohne mein Zutun und meine Kraft gewährt hat.' }] },
        { id: 3, title: 'Beim Anziehen eines neuen Kleidungsstücks', duas: [{ arabic: 'اللَّهُمَّ لَكَ الْحَمْدُ أَنْتَ كَسَوْتَنِيهِ، أَسْأَلُكَ مِنْ خَيْرِهِ وَخَيْرِ مَا صُنِعَ لَهُ، وَأَعُوذُ بِكَ مِنْ شَرِّهِ وَشَرِّ مَا صُنِعَ لَهُ', transliteration: 'Allahumma lakal-hamdu anta kasawtanihi, as`aluka min khayrihi wa khayri ma suni`a lah, wa a`udhu bika min sharrihi wa sharri ma suni`a lah.', translation: 'O Allah, Dir gebührt alles Lob. Du hast mich damit bekleidet. Ich bitte Dich um sein Gutes und das Gute, wofür es gemacht wurde, und ich suche Zuflucht bei Dir vor seinem Schlechten und dem Schlechten, wofür es gemacht wurde.' }] },
        { id: 4, title: 'Gebet für jemanden, der ein neues Kleidungsstück trägt', duas: [{ arabic: 'تُبْلِي وَيُخْلِفُ اللَّهُ تَعَالَى', transliteration: 'Tubli wa yukhlifullahu ta`ala.', translation: 'Mögest du es abtragen und möge Allah der Erhabene es ersetzen.' }] },
        { id: 5, title: 'Vor dem Ausziehen der Kleidung', duas: [{ arabic: 'بِسْمِ اللَّهِ', transliteration: 'Bismillah.', translation: 'Im Namen Allahs.' }] },
        { id: 6, title: 'Vor dem Betreten der Toilette', duas: [{ arabic: '[بِسْمِ اللهِ] اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنَ الْخُبُثِ وَالْخَبَائِثِ', transliteration: '[Bismillah] Allahumma inni a`udhu bika minal-khubuthi wal-khaba`ith.', translation: '[Im Namen Allahs] O Allah, ich suche Zuflucht bei Dir vor den männlichen und weiblichen Übeln (Dämonen).' }] },
        { id: 7, title: 'Nach dem Verlassen der Toilette', duas: [{ arabic: 'غُفْرَانَكَ', transliteration: 'Ghufranak.', translation: '(Ich bitte um) Deine Vergebung.' }] },
        { id: 8, title: 'Vor der Gebetswaschung (Wudu)', duas: [{ arabic: 'بِسْمِ اللَّهِ', transliteration: 'Bismillah.', translation: 'Im Namen Allahs.' }] },
        { id: 9, title: 'Nach der Gebetswaschung (Wudu)', duas: [{ arabic: 'أَشْهَدُ أَنْ لاَ إِلَهَ إِلاَّ اللَّهُ وَحْدَهُ لاَ شَرِيكَ لَهُ وَأَشْهَدُ أَنَّ مُحَمَّدًا عَبْدُهُ وَرَسُولُهُ', transliteration: 'Ashhadu an la ilaha illallah, wahdahu la sharika lah, wa ashhadu anna Muhammadan `abduhu wa rasuluh.', translation: 'Ich bezeuge, dass es keine Gottheit gibt außer Allah allein, ohne Partner, und ich bezeuge, dass Muhammad Sein Diener und Gesandter ist.' }] },
        { id: 10, title: 'Beim Verlassen des Hauses', duas: [{ arabic: 'بِسْمِ اللهِ، تَوَكَّلْتُ عَلَى اللهِ، وَلَا حَوْلَ وَلَا قُوَّةَ إِلَّا بِاللهِ', transliteration: 'Bismillahi, tawakkaltu `alal-lah, wa la hawla wa la quwwata illa billah.', translation: 'Im Namen Allahs, ich vertraue auf Allah, und es gibt keine Macht noch Kraft außer bei Allah.' }] },
        { id: 11, title: 'Beim Betreten des Hauses', duas: [{ arabic: 'بِسْمِ اللهِ وَلَجْنَا، وَبِسْمِ اللهِ خَرَجْنَا، وَعَلَى اللهِ رَبَّنَا تَوَكَّلْنَا', transliteration: 'Bismillahi walajna, wa bismillahi kharajna, wa `ala-Lahi rabbina tawakkalna.', translation: 'Im Namen Allahs treten wir ein, und im Namen Allahs treten wir aus, und auf Allah, unseren Herrn, vertrauen wir.' }] },
        { id: 12, title: 'Gebet beim Gang zur Moschee', duas: [{ arabic: 'اللَّهُمَّ اجْعَلْ فِي قَلْبِي نُورًا، وَفِي لِسَانِي نُورًا، وَفِي سَمْعِي نُورًا، وَفِي بَصَرِي نُورًا، وَمِنْ فَوْقِي نُورًا، وَمِنْ تَحْتِي نُورًا، وَعَنْ يَمِينِي نُورًا، وَعَنْ شِمَالِي نُورًا، وَمِنْ أَمَامِي نُورًا، وَمِنْ خَلْفِي نُورًا، وَاجْعَلْ فِي نَفْسِي نُورًا، وَأَعْظِمْ لِي NūRًا، وَعَظِّمْ لِي نُورًا، وَاجْعَلْ لِي نُورًا، وَاجْعَلْنِي نُورًا، اللَّهُمَّ أَعْطِنِي نُورًا، وَاجْعَلْ فِي عَصَبِي نُورًا، وَفِي لَحْمِي نُورًا، وَفِي دَمِي نُورًا، وَفِي شَعْرِي نُورًا، وَفِي بَشَرِي نُورًا', transliteration: 'Allahumma-j`al fi qalbi nuran, wa fi lisani nuran, wa fi sam`i nuran, wa fi basari nuran, wa min fawqi nuran, wa min tahti nuran, wa `an yamini nuran, wa `an shimali nuran, wa min amami nuran, wa min khalfi nuran, waj`al fi nafsi nuran, wa a`zim li nuran, wa `azzim li nuran, waj`al li nuran, waj`alni nuran, Allahumma a`tini nuran, waj`al fi `asabi nuran, wa fi lahmi nuran, wa fi dami nuran, wa fi sha`ri nuran, wa fi bashari nuran.', translation: 'O Allah, mache Licht in mein Herz, Licht auf meine Zunge, Licht in mein Gehör, Licht in mein Sehen, Licht über mir, Licht unter mir, Licht zu meiner Rechten, Licht zu meiner Linken, Licht vor mir, Licht hinter mir, und mache Licht in meine Seele, und gib mir großes Licht, und mache mir großes Licht, mache mir Licht und mache mich zu Licht. O Allah, gib mir Licht, und mache Licht in meine Nerven, und Licht in mein Fleisch, und Licht in mein Blut, und Licht in mein Haar und Licht in meine Haut.' }] },
        { id: 13, title: 'Beim Betreten der Moschee', duas: [{ arabic: 'أَعُوذُ بِاللهِ الْعَظِيمِ، وَبِوَجْهِهِ الْكَرِيمِ، وَسُلْطَانِهِ الْقَدِيمِ، مِنَ الشَّيْطَانِ الرَّجِيمِ. [بِسْمِ اللَّهِ، وَالصَّلَاةُ وَالسَّلَامُ عَلَى رَسُولِ اللَّهِ]. اللَّهُمَّ افْتَحْ لِي أَبْوَابَ رَحْمَتِكَ', transliteration: 'A`udhu billahil-`azim, wa bi-wajhihil-karim, wa sultanihil-qadim, minash-shaytanir-rajim. [Bismil-lah, was-salatu was-salamu `ala rasulil-lah]. Allahumma-ftah li abwaba rahmatik.', translation: 'Ich suche Zuflucht bei Allah, dem Allmächtigen, und bei Seinem edlen Antlitz und Seiner ewigen Herrschaft, vor dem verfluchten Satan. [Im Namen Allahs, und Segen und Frieden seien auf dem Gesandten Allahs]. O Allah, öffne mir die Tore Deiner Barmherzigkeit.' }] },
        { id: 14, title: 'Beim Verlassen der Moschee', duas: [{ arabic: 'بِسْمِ اللهِ وَالصَّلَاةُ وَالسَّلَامُ عَلَى رَسُولِ اللهِ، اللَّهُمَّ إِنِّي أَسْأَلُكَ مِنْ فَضْلِكَ، اللَّهُمَّ اعْصِمْنِي مِنَ الشَّيْطَانِ الرَّجِيمِ', transliteration: 'Bismillahi was-salatu was-salamu `ala rasulillah, allahumma inni as`aluka min fadlik, allahumma i`simni minash-shaytanir-rajim.', translation: 'Im Namen Allahs, und Segen und Frieden seien auf dem Gesandten Allahs. O Allah, ich bitte Dich um Deine Gunst. O Allah, schütze mich vor dem verfluchten Satan.' }] },
        { id: 15, title: 'Gebetsruf (Adhan)', duas: [{ arabic: 'يَقُولُ مِثْلَ مَا يَقُولُ الْمُؤَذِّنُ إِلاَّ فِي "حَيَّ عَلَى الصَّلاةِ وَحَيَّ عَلَى الْفَلاحِ". فَيقُولُ: "لا حَوْلَ وَلا قُوَّةَ إِلا بِاللهِ".', transliteration: 'Man wiederholt, was der Muezzin sagt, außer bei "Hayya `alas-salah" und "Hayya `alal-falah", wo man sagt: "La hawla wa la quwwata illa billah".', translation: 'Wiederhole, was der Gebetsrufer sagt, außer wenn er "Hayya \'alas-salah" (Eilt zum Gebet) und "Hayya \'alal-falah" (Eilt zum Erfolg) sagt. Stattdessen sollte man sagen: "Es gibt keine Macht und keine Kraft außer bei Allah".' }] },
        { id: 16, title: 'Nach dem Gebetsruf', duas: [{ arabic: 'اللَّهُمَّ رَبَّ هَذِهِ الدَّعْوَةِ التَّامَّةِ، وَالصَّلَاةِ الْقَائِمَةِ، آتِ مُحَمَّدًا الْوَسِيلَةَ وَالْفَضِيلَةَ، وَابْعَثْهُ مَقَامًا مَحْمُودًا الَّذِي وَعَدْتَهُ، [إِنَّكَ لَا تُخْلِفُ الْمِيعَادَ]', transliteration: 'Allahumma rabba hadhihid-da`watit-tammah, was-salatil-qa`imah, ati Muhammadanil-wasilata wal-fadilah, wab`athhu maqaman mahmudanil-ladhi wa`adtah, [innaka la tukhliful-mi`ad].', translation: 'O Allah, Herr dieses vollkommenen Rufes und dieses aufrechten Gebets, gewähre Muhammad die Wasilah (höchste Stufe im Paradies) und die Fadhilah (besondere Ehre) und erwecke ihn zu dem gepriesenen Rang, den Du ihm versprochen hast.' }] },
        { id: 17, title: 'Bei Sorgen und Kummer', duas: [{ arabic: 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنَ الْهَمِّ وَالْحَзَنِ، وَالْعَجْزِ وَالْكَسَلِ، وَالْبُخْلِ وَالْجُبْنِ، وَضَلَعِ الدَّيْنِ وَغَلَبَةِ الرِّجَالِ', transliteration: 'Allahumma inni a`udhu bika minal-hammi wal-hazan, wal-`ajzi wal-kasal, wal-bukhli wal-jubn, wa dala`id-dayni wa ghalabatir-rijal.', translation: 'O Allah, ich suche Zuflucht bei Dir vor Sorge und Kummer, vor Unfähigkeit und Faulheit, vor Geiz und Feigheit, vor der Last der Schulden und der Überwältigung durch Menschen.' }] },
        { id: 18, title: 'Bei Bedrängnis', duas: [{ arabic: 'لَا إِلَهَ إِلَّا اللَّهُ الْعَظِيمُ الْحَلِيمُ، لَا إِلَهَ إِلَّا اللَّهُ رَبُّ الْعَرْشِ الْعَظِيمِ، لَا إِلَهَ إِلَّا اللَّهُ رَبُّ السَّمَاوَاتِ وَرَبُّ الْأَرْضِ وَرَبُّ الْعَرْشِ الْكَرِيمِ', transliteration: 'La ilaha illallahul-`azimul-halim, la ilaha illallahu rabbul-`arshil-`azim, la ilaha illallahu rabbus-samawati wa rabbul-ardi wa rabbul-`arshil-karim.', translation: 'Es gibt keine Gottheit außer Allah, dem Allmächtigen, dem Nachsichtigen. Es gibt keine Gottheit außer Allah, dem Herrn des gewaltigen Throns. Es gibt keine Gottheit außer Allah, dem Herrn der Himmel und dem Herrn der Erde und dem Herrn des edlen Throns.' }] },
        { id: 19, title: 'Beim Treffen eines Feindes oder Herrschers', duas: [{ arabic: 'اللَّهُمَّ إِنَّا نَجْعَلُكَ فِي نُحُورِهِمْ وَنَعُوذُ بِكَ مِنْ شُرُورِهِمْ', transliteration: 'Allahumma inna naj`aluka fi nuhurihim wa na`udhu bika min shururihim.', translation: 'O Allah, wir stellen Dich ihnen entgegen und suchen Zuflucht bei Dir vor ihrem Übel.' }] },
        { id: 20, title: 'Bei Angst vor einem Herrscher', duas: [{ arabic: 'اللَّهُمَّ رَبَّ السَّمَاوَاتِ السَّبْعِ، وَرَبَّ الْعَرْشِ الْعَظِيمِ، كُنْ لِي جَارًا مِنْ فُلاَنِ بْنِ فُلاَنٍ، وَأَحْزَابِهِ مِنْ خَلاَئِقِكَ، أَنْ يَفْرُطَ عَلَيَّ أَحَدٌ مِنْهُمْ أَوْ يَطْغَى، عَزَّ جَارُكَ، وَجَلَّ ثَنَاؤُكَ، وَلاَ إِلَهَ إِلاَّ أَنْتَ', transliteration: 'Allahumma rabbas-samawatis-sab`, wa rabbal-`arshil-`azim, kun li jaran min [Name der Person], wa ahzabihi min khala`iqik, an yafruta `alayya ahadun minhum aw yatgha, `azza jaruk, wa jalla thana`uk, wa la ilaha illa ant.', translation: 'O Allah, Herr der sieben Himmel, Herr des gewaltigen Throns, sei mein Beschützer vor [Name der Person] und ihren/seinen Anhängern unter Deinen Geschöpfen, damit keiner von ihnen mich überfällt oder übertritt. Mächtig ist Dein Schutz, und herrlich ist Dein Lob, und es gibt keine Gottheit außer Dir.' }] },
        { id: 21, title: 'Gegen Feinde', duas: [{ arabic: 'اللَّهُمَّ مُنْزِلَ الْكِتَابِ، سَرِيعَ الْحِسَابِ، اهْزِمِ الأَحْزَابَ، اللَّهُمَّ اهْزِمْهُمْ وَزَلْزِلْهُمْ', transliteration: 'Allahumma munzilal-kitab, sari`al-hisab, ihzimil-ahzab, allahumma ihzimhum wa zalzilhum.', translation: 'O Allah, Offenbarer des Buches, schnell im Abrechnen, besiege die Verbündeten. O Allah, besiege sie und erschüttere sie.' }] },
        { id: 22, title: 'Bei Angst vor einer Gruppe von Menschen', duas: [{ arabic: 'اللَّهُمَّ اكْفِنِيهِمْ بِمَا شِئْتَ', transliteration: 'Allahummak-finihim bima shi`t.', translation: 'O Allah, genüge mir gegen sie, wie auch immer Du willst.' }] },
        { id: 23, title: 'Bei Zweifeln im Glauben', duas: [{ arabic: 'يَسْتَعِيذُ بِاللهِ. يَنْتَهِي عَمَّا شَكَّ فِيهِ. يَقُولُ: آمَنْتُ بِاللهِ وَرُسُلِهِ. يَقْرَأُ قَوْلَ اللهِ تَعَالَى: {هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْبَاطِنُ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ}', transliteration: 'Suche Zuflucht bei Allah. Beende, woran du zweifelst. Sage: "Ich glaube an Allah und Seine Gesandten." Lies die Worte Allahs des Erhabenen: "Er ist der Erste und der Letzte, der Offenbare und der Verborgene, und Er ist über alles wissend."', translation: 'Er sollte Zuflucht bei Allah suchen. Er sollte aufhören, woran er zweifelt. Er sollte sagen: "Ich glaube an Allah und Seine Gesandten." Er sollte den Vers lesen: "Er ist der Erste und der Letzte, der Offenbare und der Verborgene, und Er ist über alles wissend." (57:3)' }] },
        { id: 24, title: 'Zur Begleichung von Schulden', duas: [{ arabic: 'اللَّهُمَّ اكْفِنِي بِحَلَالِكَ عَنْ حَرَامِكَ، وَأَغْنِنِي بِفَضْلِكَ عَمَّنْ سِوَاكَ', transliteration: 'Allahummak-fini bi-halalika `an haramik, wa aghnini bi-fadlika `amman siwak.', translation: 'O Allah, genüge mir mit dem, was Du erlaubt hast, gegenüber dem, was Du verboten hast, und mache mich durch Deine Gunst unabhängig von allen anderen außer Dir.' }] },
        { id: 25, title: 'Bei Einflüsterungen im Gebet oder beim Lesen', duas: [{ arabic: 'أَعُوذُ بِاللهِ مِنَ الشَّيْطَانِ الرَّجِيمِ وَاتْفُلْ عَلَى يَسَارِكَ (ثَلاثاً)', transliteration: 'A`udhu billahi minash-shaytanir-rajim (und spucke dreimal leicht nach links).', translation: 'Ich suche Zuflucht bei Allah vor dem verfluchten Satan (und spucke dreimal leicht nach links).' }] },
        { id: 26, title: 'Wenn etwas schwierig wird', duas: [{ arabic: 'اللَّهُمَّ لَا سَهْلَ إِلَّا مَا جَعَلْتَهُ sَهْلًا، وَأَنْتَ تَجْعَلُ الْحَزْنَ إِذَا شِئْتَ سَهْلًا', transliteration: 'Allahumma la sahla illa ma ja`altahu sahla, wa anta taj`alul-hazna idha shi`ta sahla.', translation: 'O Allah, nichts ist leicht, außer dem, was Du leicht machst, und Du machst die Sorge, wenn Du willst, leicht.' }] },
        { id: 27, title: 'Nach einer Sünde', duas: [{ arabic: 'مَا مِنْ عَبْدٍ يُذْنِبُ ذَنْباً فَيُحْسِنُ الطُّهُورَ، ثُمَّ يَقُومُ فَيُصَلِّي رَكْعَتَيْنِ، ثُمَّ يَسْتَغْفِرُ اللهَ إِلاَّ غَفَرَ اللهُ لَهُ', transliteration: 'Kein Diener begeht eine Sünde, reinigt sich dann gut, steht auf und betet zwei Rak`ah und bittet dann Allah um Vergebung, ohne dass Allah ihm vergibt.', translation: 'Jeder Diener, der eine Sünde begeht und sich dann gründlich reinigt, dann aufsteht und zwei Rak`ahs betet und dann Allah um Vergebung bittet, dem wird Allah vergeben.' }] },
        { id: 28, title: 'Zum Schutz vor dem Satan', duas: [{ arabic: 'الاِسْتِعَاذَةُ بِاللهِ مِنْهُ. الأَذَانُ. الأَذْكَارُ وَقِرَاءَةُ الْقُرْآنِ.', transliteration: 'Zuflucht bei Allah vor ihm suchen. Der Adhan (Gebetsruf). Dhikr (Gedenken an Allah) und das Lesen des Korans.', translation: 'Zuflucht bei Allah vor ihm suchen. Der Gebetsruf. Das Gedenken an Allah und das Lesen des Korans.' }] },
        { id: 29, title: 'Wenn etwas Unerwünschtes geschieht', duas: [{ arabic: 'قَدَرُ اللهِ وَمَا شَاءَ فَعَلَ', transliteration: 'Qaddarullahu wa ma sha`a fa`al.', translation: 'Es ist das Schicksal Allahs, und was Er will, tut Er.' }] },
        { id: 30, title: 'Glückwünsche für ein Neugeborenes', duas: [{ arabic: 'بَارَكَ اللهُ لَكَ فِي الْمَوْهُوبِ لَكَ، وَشَكَرْتَ الْوَاهِبَ، وَبَلَغَ أَشُدَّهُ، وَرُزِقْتَ بِرَّهُ', transliteration: 'Barakallahu laka fil-mawhubi lak, wa shakartal-wahib, wa balagha ashuddah, wa ruziqta birrah.', translation: 'Möge Allah dich mit Seiner Gabe segnen, mögest du dem Geber danken, möge das Kind seine volle Kraft erreichen und mögest du mit seiner Rechtschaffenheit versorgt werden.' }] },
        { id: 31, title: 'Zum Schutz von Kindern', duas: [{ arabic: 'أُعِيذُكُمَا بِكَلِمَاتِ اللهِ التَّامَّةِ مِنْ كُلِّ شَيْطَانٍ وَهَامَّةٍ، وَمِنْ كُلِّ عَيْنٍ لَامَّةٍ', transliteration: 'U`idhukuma bikalimatil-lahit-tammati min kulli shaytanin wa hammah, wa min kulli `aynin lammah.', translation: 'Ich suche für euch beide Zuflucht bei den vollkommenen Worten Allahs vor jedem Satan und jedem giftigen Tier und vor jedem bösen Blick.' }] },
        { id: 32, title: 'Beim Besuch eines Kranken', duas: [{ arabic: 'لَا بَأْسَ طَهُورٌ إِنْ شَاءَ اللهُ', transliteration: 'La ba`sa, tahurun in sha` Allah.', translation: 'Keine Sorge, es ist eine Reinigung, so Allah will.' }, { arabic: 'أَسْأَلُ اللهَ الْعَظِيمَ رَبَّ الْعَرْشِ الْعَظِيمِ أَنْ يَشْفِيَكَ', transliteration: 'As`alullahal-`azim, rabbal-`arshil-`azim, an yashfiyak.', translation: 'Ich bitte Allah, den Allmächtigen, den Herrn des gewaltigen Throns, dich zu heilen (7-mal sagen).' }] },
        { id: 33, title: 'Tugend des Krankenbesuchs', duas: [{ arabic: 'إِذَا عَادَ الرَّجُلُ أَخَاهُ الْمُسْلِمَ مَشَى فِي خِرَافَةِ الْجَنَّةِ حَتَّى يَجْلِسَ، فَإِذَا جَلَسَ غَمَرَتْهُ الرَّحْمَةُ، فَإِنْ كَانَ غُدْوَةً صَلَّى عَلَيْهِ سَبْعُونَ أَلْفَ مَلَكٍ حَتَّى يُمْسِيَ، وَإِنْ كَانَ مَسَاءً صَلَّى عَلَيْهِ سَبْعُونَ أَلْفَ مَلَكٍ حَتَّى يُصْبِحَ', transliteration: 'Wenn ein Mann seinen muslimischen Bruder besucht, geht er sozusagen unter den Früchten des Paradieses spazieren, bis er sich setzt, und wenn er sich setzt, umhüllt ihn die Barmherzigkeit. Wenn es am Morgen ist, beten siebzigtausend Engel für ihn bis zum Abend, und wenn es am Abend ist, beten siebzigtausend Engel für ihn bis zum Morgen.', translation: 'Wenn ein Mann seinen muslimischen Bruder besucht, wandelt er inmitten der Früchte des Paradieses, bis er sich setzt. Wenn er sich setzt, wird er von Barmherzigkeit umgeben. Wenn er ihn am Morgen besucht, beten siebzigtausend Engel für ihn bis zum Abend, und wenn er ihn am Abend besucht, beten siebzigtausend Engel für ihn bis zum Morgen.' }] },
        { id: 34, title: 'Gebet eines Kranken, der die Hoffnung aufgegeben hat', duas: [{ arabic: 'اللَّهُمَّ اغْفِرْ لِي وَارْحَمْنِي وَأَلْحِقْنِي بِالرَّفِيقِ الْأَعْلَى', transliteration: 'Allahummagh-fir li warhamni wa alhiqni bir-rafiqil-a`la.', translation: 'O Allah, vergib mir und erbarme Dich meiner und führe mich zum höchsten Gefährten.' }] },
        { id: 35, title: 'Anleitung für eine sterbende Person', duas: [{ arabic: 'مَنْ كَانَ آخِرُ كَلَامِهِ لَا إِلَهَ إِلَّا اللَّهُ dَخَلَ الْجَنَّةَ', transliteration: 'Man kana akhiru kalamihi "La ilaha illallah" dakhalal-jannah.', translation: 'Wessen letzte Worte "La ilaha illallah" sind, wird das Paradies betreten.' }] },
        { id: 36, title: 'Bei einem Unglück', duas: [{ arabic: 'إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَاجِعُونَ، اللَّهُمَّ أْجُرْنِي فِي مُصِيبَتِي، وَأَخْلِفْ لِي خَيْرًا مِنْهَا', transliteration: 'Inna lillahi wa inna ilayhi raji`un. Allahumma-`jurni fi musibati, wa akhlif li khayran minha.', translation: 'Wahrlich, wir gehören Allah, und zu Ihm werden wir zurückkehren. O Allah, belohne mich für mein Unglück und ersetze es mir durch etwas Besseres.' }] },
        { id: 37, title: 'Beim Schließen der Augen des Verstorbenen', duas: [{ arabic: 'اللَّهُمَّ اغْفِرْ لِفُلاَنٍ (بِاسْمِهِ) وَارْفَعْ دَرَجَتَهُ فِي الْمَهْدِيِّينَ، وَاخْلُفْهُ فِي عَقِبِهِ فِي الْغَابِرِينَ، وَاغْفِرْ لَنَا وَلَهُ يَا رَبَّ الْعَالَمِينَ، وَافْسَحْ لَهُ فِي قَبْرِهِ وَنَوِّرْ لَهُ فِيهِ', transliteration: 'Allahummagh-fir li [Name der Person], warfa` darajatahu fil-mahdiyyin, wakhlufhu fi `aqibihi fil-ghabirin, waghfir lana wa lahu ya rabbal-`alamin, wafsah lahu fi qabrihi wa nawwir lahu fih.', translation: 'O Allah, vergib [Name der Person] und erhöhe seinen Rang unter den Rechtgeleiteten und sei sein Nachfolger unter denen, die zurückbleiben. Und vergib uns und ihm, o Herr der Welten. Mache sein Grab geräumig und erleuchte es für ihn.' }] },
        { id: 38, title: 'Gebet für den Verstorbenen im Totengebet', duas: [{ arabic: 'اللَّهُمَّ اغْفِرْ لَهُ وَارْحَمْهُ، وَعَافِهِ وَاعْفُ عَنْهُ، وَأَكْرِمْ نُزُلَهُ، وَوَسِّعْ مُدْخَلَهُ، وَاغْسِلْهُ بِالْمَاءِ وَالثَّلْجِ وَالْبَرَدِ، وَنَقِّهِ مِنَ الْخَطَايَا كَمَا نَقَّيْتَ الثَّوْبَ الْأَبْيَضَ مِنَ الدَّنَسِ، وَأَبْدِلْهُ دَارًا خَيْرًا مِنْ دَارِهِ، وَأَهْلًا خَيْرًا مِنْ أَهْلِهِ، وَزَوْجًا خَيْرًا مِنْ زَوْجِهِ، وَأَدْخِلْهُ الْجَنَّةَ، وَأَعِذْهُ مِنْ عَذَابِ الْقَبْرِ وَعَذَابِ النَّارِ', transliteration: 'Allahummagh-fir lahu warhamh, wa `afihi wa`fu `anh, wa akrim nuzulah, wa wassi` mudkhalah, waghsilhu bil-ma`i wath-thalji wal-barad, wa naqqihi minal-khataya kama naqqaytath-thawbal-abyada minad-danas, wa abdilhu daran khayran min darih, wa ahlan khayran min ahlih, wa zawjan khayran min zawjih, wa adkhilhul-jannah, wa a`idhhu min `adhabil-qabri wa `adhabin-nar.', translation: 'O Allah, vergib ihm und erbarme Dich seiner, schenke ihm Wohlbefinden und verzeihe ihm. Ehre seinen Aufenthalt und mache seinen Eingang weit. Wasche ihn mit Wasser, Schnee und Hagel und reinige ihn von Sünden, wie Du ein weißes Gewand von Schmutz reinigst. Gib ihm ein besseres Zuhause als sein Zuhause, eine bessere Familie als seine Familie und einen besseren Ehepartner als seinen Ehepartner. Führe ihn ins Paradies ein und schütze ihn vor der Qual des Grabes und der Qual des Feuers.' }] },
        { id: 39, title: 'Bei der Beerdigung des Verstorbenen', duas: [{ arabic: 'بِسْمِ اللَّهِ وَعَلَى سُنَّةِ رَسُولِ اللَّهِ', transliteration: 'Bismillahi wa `ala sunnati rasulillah.', translation: 'Im Namen Allahs und gemäß der Sunnah des Gesandten Allahs.' }] },
        { id: 40, title: 'Nach der Beerdigung', duas: [{ arabic: 'اللَّهُمَّ اغْفِرْ لَهُ اللَّهُمَّ ثَبِّتْهُ', transliteration: 'Es wird empfohlen, am Grab zu stehen und für den Verstorbenen um Vergebung und Standhaftigkeit zu bitten.', translation: 'Es wird empfohlen, am Grab zu verweilen und für den Verstorbenen um Vergebung und Standhaftigkeit bei der Befragung zu bitten.' }] },
        { id: 41, title: 'Beim Besuch von Gräbern', duas: [{ arabic: 'السَّلَامُ عَلَيْكُمْ أَهْلَ الدِّيَارِ مِنَ الْمُؤْمِنِينَ وَالْمُSْلِمِينَ، وَإِنَّا إِنْ شَاءَ اللَّهُ بِكُمْ لَاحِقُونَ، أَسْأَلُ اللَّهَ لَنَا وَلَكُمُ الْعَافِيَةَ', transliteration: 'As-salamu `alaykum ahladdiyari minal-mu`minina wal-muslimin, wa inna in sha` Allahu bikum lahiqun, as`alullaha lana wa lakumul-`afiyah.', translation: 'Friede sei mit euch, o Bewohner dieser Wohnstätten, unter den Gläubigen und Muslimen. Wahrlich, wir werden euch, so Allah will, folgen. Wir bitten Allah um Wohlbefinden für uns und für euch.' }] },
        { id: 42, title: 'Bei starkem Wind', duas: [{ arabic: 'اللَّهُمَّ إِنِّي أَسْأَلُكَ خَيْرَهَا، وَأَعُوذُ بِكَ مِنْ شَرِّهَا', transliteration: 'Allahumma inni as`aluka khayraha, wa a`udhu bika min sharriha.', translation: 'O Allah, ich bitte Dich um ihr Gutes und suche Zuflucht bei Dir vor ihrem Schlechten.' }] },
        { id: 43, title: 'Bei Donner', duas: [{ arabic: 'سُبْحَانَ الَّذِي يُسَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلَائِكَةُ مِنْ خِيفَتِهِ', transliteration: 'Subhanal-ladhi yusabbihur-ra`du bi-hamdihi wal-mala`ikatu min khifatih.', translation: 'Gepriesen sei Der, Dessen Lob der Donner preist und auch die Engel aus Furcht vor Ihm.' }] },
        { id: 44, title: 'Bei Regen', duas: [{ arabic: 'اللَّهُمَّ صَيِّبًا نَافِعًا', transliteration: 'Allahumma sayyiban nafi`a.', translation: 'O Allah, (mache es zu) einem nützlichen Regen.' }] },
        { id: 45, title: 'Nach dem Regen', duas: [{ arabic: 'مُطِرْنَا بِفَضْلِ اللَّهِ وَرَحْمَتِهِ', transliteration: 'Mutirna bi-fadlillahi wa rahmatih.', translation: 'Wir wurden durch Allahs Gnade und Barmherzigkeit mit Regen beschenkt.' }] },
        { id: 46, title: 'Beim Anblick des Neumondes', duas: [{ arabic: 'اللَّهُ أَكْبَرُ، اللَّهُمَّ أَهِلَّهُ عَلَيْنَا بِالْأَمْنِ وَالْإِيمَانِ، وَالسَّلَامَةِ وَالْإِسْلَامِ، وَالتَّوْفِيقِ لِمَا تُحِبُّ رَبَّنَا وَتَرْضَى، رَبُّنَا وَرَبُّكَ اللَّهُ', transliteration: 'Allahu akbar, Allahumma ahillahu `alayna bil-amni wal-iman, was-salamati wal-islam, wat-tawfiqi lima tuhibbu rabbana wa tarda, rabbuna wa rabbukallah.', translation: 'Allah ist der Größte. O Allah, lass diesen Mond über uns aufgehen mit Sicherheit und Glauben, mit Frieden und Islam und mit Erfolg in dem, was Du, unser Herr, liebst und womit Du zufrieden bist. Unser Herr und dein Herr ist Allah.' }] },
        { id: 47, title: 'Beim Fastenbrechen', duas: [{ arabic: 'ذَهَبَ الظَّمَأُ وَابْتَلَّتِ الْعُرُوقُ، وَثَبَتَ الْأَjْرُ إِنْ شَاءَ اللَّهُ', transliteration: 'Dhahabaz-zama`u wabtallatil-`uruq, wa thabatal-ajru in sha` Allah.', translation: 'Der Durst ist vergangen, die Adern sind befeuchtet, und die Belohnung ist sicher, so Allah will.' }] },
        { id: 48, title: 'Vor dem Essen', duas: [{ arabic: 'بِسْمِ اللَّهِ', transliteration: 'Bismillah.', translation: 'Im Namen Allahs.' }] },
        { id: 49, title: 'Nach dem Essen', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي أَطْعَمَنِي هَذَا وَرَزَقَنِيهِ مِنْ غَيْرِ حَوْلٍ مِنِّي وَلَا قُوَّةٍ', transliteration: 'Alhamdu lillahil-ladhi at`amani hadha wa razaqanihi min ghayri hawlin minni wa la quwwah.', translation: 'Alles Lob gebührt Allah, der mich dies hat essen lassen und mich damit versorgt hat, ohne Macht und Kraft meinerseits.' }] },
        { id: 50, title: 'Gebet eines Gastes für den Gastgeber', duas: [{ arabic: 'اللَّهُمَّ بَارِكْ لَهُمْ فِيمَا رَزَقْتَهُمْ، وَاغْفِرْ لَهُمْ وَارْحَمْهُمْ', transliteration: 'Allahumma barik lahum fima razaqtahum, waghfir lahum warhamhum.', translation: 'O Allah, segne sie in dem, was Du ihnen gegeben hast, vergib ihnen und erbarme Dich ihrer.' }] },
        { id: 51, title: 'Beim Niesen', duas: [{ arabic: 'إِذَا عَطَسَ أَحَدُكُمْ فَلْيَقُلْ: الْحَمْدُ لِلَّهِ، وَلْيَقُلْ لَهُ أَخُوهُ أَوْ صَاحِبُهُ: يَرْحَمُكَ اللَّهُ. فَإِذَا قَالَ لَهُ: يَرْحَمُكَ اللَّهُ، فَلْيَقُلْ: يَهْدِيكُمُ اللَّهُ وَيُصْلِحُ بَالَكُمْ', transliteration: 'Wenn einer von euch niest, soll er sagen: "Alhamdulillah". Sein Bruder oder Gefährte soll zu ihm sagen: "Yarhamukallah". Wenn er "Yarhamukallah" sagt, soll er antworten: "Yahdikumullahu wa yuslihu balakum".', translation: 'Wenn einer von euch niest, soll er sagen: "Alhamdulillah" (Alles Lob gebührt Allah). Und sein Bruder oder Gefährte soll zu ihm sagen: "Yarhamukallah" (Möge Allah dir barmherzig sein). Wenn er zu ihm "Yarhamukallah" sagt, soll er antworten: "Yahdikumullahu wa yuslihu balakum" (Möge Allah euch rechtleiten und euren Zustand verbessern).' }] },
        { id: 52, title: 'Glückwünsche zur Hochzeit', duas: [{ arabic: 'بَارَكَ اللَّهُ لَكَ، وَبَارَكَ عَلَيْكَ، وَجَمَعَ بَيْنَكُمَا فِي خَيْرٍ', transliteration: 'Barakallahu lak, wa baraka `alayk, wa jama`a baynakuma fi khayr.', translation: 'Möge Allah dich segnen, und möge Er Seinen Segen über dich ausgießen, und möge Er euch beide im Guten vereinen.' }] },
        { id: 53, title: 'Bei Ärger', duas: [{ arabic: 'أَعُوذُ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ', transliteration: 'A`udhu billahi minash-shaytanir-rajim.', translation: 'Ich suche Zuflucht bei Allah vor dem verfluchten Satan.' }] },
        { id: 54, title: 'Wenn man jemanden in Not sieht', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي عَافَانِي مِمَّا ابْتَلَاكَ بِهِ، وَفَضَّلَنِي عَلَى كَثِيرٍ مِمَّنْ خَلَقَ تَفْضِيلًا', transliteration: 'Alhamdu lillahil-ladhi `afani mimmab-talaka bih, wa faddalani `ala kathirin mimman khalaqa tafdila.', translation: 'Alles Lob gebührt Allah, der mich vor dem bewahrt hat, womit Er dich geprüft hat, und mich vor vielen Seiner Geschöpfe bevorzugt hat.' }] },
        { id: 55, title: 'Was man in einer Versammlung sagt', duas: [{ arabic: 'عَنِ ابْنِ عُمَرَ، قَالَ: كَانَ يُعَدُّ لِرَسُولِ اللَّهِ ﷺ فِي الْمَجْلِسِ الْوَاحِدِ مِائَةُ مَرَّةٍ مِنْ قَبْلِ أَنْ يَقُومَ: رَبِّ اغْفِرْ لِي وَتُبْ عَلَيَّ إِنَّكَ أَنْتَ التَّوَّابُ الْغَفُورُ', transliteration: 'Man zählte, dass der Gesandte Allahs ﷺ in einer einzigen Sitzung hundertmal sagte, bevor er aufstand: "Rabbi-ghfir li wa tub `alayya, innaka antat-tawwabul-ghafur."', translation: 'Es wurde berichtet, dass der Gesandte Allahs ﷺ in einer einzigen Sitzung hundertmal sagte, bevor er aufstand: "Mein Herr, vergib mir und nimm meine Reue an, wahrlich, Du bist der Reue-Annehmende, der Vergebende."' }] },
        { id: 56, title: 'Sühne für eine Versammlung (Kaffaratul-Majlis)', duas: [{ arabic: 'سُبْحَانَكَ اللَّهُمَّ وَبِحَمْدِكَ، أَشْهَدُ أَنْ لَا إِلَهَ إِلَّا أَنْتَ، أَسْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', transliteration: 'Subhanakallahumma wa bihamdik, ashhadu an la ilaha illa ant, astaghfiruka wa atubu ilayk.', translation: 'Gepriesen seist Du, o Allah, und mit Deinem Lob. Ich bezeuge, dass es keine Gottheit gibt außer Dir. Ich bitte Dich um Vergebung und bereue vor Dir.' }] },
        { id: 57, title: 'Für jemanden, der sagt "Möge Allah dir vergeben"', duas: [{ arabic: 'وَلَكَ', transliteration: 'Wa lak.', translation: 'Und dir auch.' }] },
        { id: 58, title: 'Für jemanden, der einem einen Gefallen getan hat', duas: [{ arabic: 'جَزَاكَ اللَّهُ خَيْرًا', transliteration: 'Jazakallahu khayra.', translation: 'Möge Allah dich mit Gutem belohnen.' }] },
        { id: 59, title: 'Zum Schutz vor dem Dajjal', duas: [{ arabic: 'مَنْ حَفِظَ عَشْرَ آيَاتٍ مِنْ أَوَّلِ سُورَةِ الْكَهْفِ عُصِمَ مِنَ الدَّجَّالِ', transliteration: 'Wer die ersten zehn Verse der Sure Al-Kahf auswendig lernt, wird vor dem Dajjal geschützt sein.', translation: 'Wer die ersten zehn Verse der Sure Al-Kahf auswendig lernt, wird vor dem Dajjal geschützt sein.' }] },
        { id: 60, title: 'Für jemanden, der sagt "Ich liebe dich um Allahs willen"', duas: [{ arabic: 'أَحَبَّكَ الَّذِي أَحْبَبْتَنِي لَهُ', transliteration: 'Ahabbakal-ladhi ahbabtani lah.', translation: 'Möge Derjenige dich lieben, um dessentwillen du mich liebst.' }] },
        { id: 61, title: 'Für jemanden, der einem sein Vermögen anbietet', duas: [{ arabic: 'بَارَكَ اللَّهُ لَكَ فِي أَهْلِكَ وَمَالِكَ', transliteration: 'Barakallahu laka fi ahlika wa malik.', translation: 'Möge Allah dich in deiner Familie und deinem Vermögen segnen.' }] },
        { id: 62, title: 'Beim Bezahlen einer Schuld', duas: [{ arabic: 'بَارَكَ اللَّهُ لَكَ فِي أَهْلِكَ وَمَالِكَ، إِنَّمَا جَزَاءُ السَّلَفِ الْحَمْدُ وَالْأَدَاءُ', transliteration: 'Barakallahu laka fi ahlika wa malik, innama jaza`us-salafil-hamdu wal-ada`.', translation: 'Möge Allah dich in deiner Familie und deinem Vermögen segnen. Wahrlich, die Belohnung für ein Darlehen ist Lob und die Rückzahlung.' }] },
        { id: 63, title: 'Aus Angst vor Shirk (Götzendienst)', duas: [{ arabic: 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ أَنْ أُشْرِكَ بِكَ وَأَنَا أَعْلَمُ، وَأَسْتَغْفِرُكَ لِمَا لَا أَعْلَمُ', transliteration: 'Allahumma inni a`udhu bika an ushrika bika wa ana a`lam, wa astaghfiruka lima la a`lam.', translation: 'O Allah, ich suche Zuflucht bei Dir davor, Dir wissentlich etwas beizugesellen, und ich bitte Dich um Vergebung für das, was ich nicht weiß.' }] },
        { id: 64, title: 'Für jemanden, der sagt "Möge Allah dich segnen"', duas: [{ arabic: 'وَفِيكَ بَارَكَ اللَّهُ', transliteration: 'Wa fika barakallah.', translation: 'Und möge Allah dich segnen.' }] },
        { id: 65, title: 'Gegen böse Omen', duas: [{ arabic: 'اللَّهُمَّ لَا طَيْرَ إِلَّا طَيْرُكَ، وَلَا خَيْرَ إِلَّا خَيْرُكَ، وَلَا إِلَهَ غَيْرُكَ', transliteration: 'Allahumma la tayra illa tayruk, wa la khayra illa khayruk, wa la ilaha ghayruk.', translation: 'O Allah, es gibt kein Omen außer Deinem Omen, und kein Gutes außer Deinem Guten, und es gibt keine Gottheit außer Dir.' }] },
        { id: 66, title: 'Beim Fahren eines Fahrzeugs', duas: [{ arabic: 'بِسْمِ اللَّهِ، الْحَمْدُ لِلَّهِ، سُبْحَانَ الَّذِي سَخَّرَ لَنَا هَذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ، وَإِنَّا إِلَى رَبِّنَا لَمُنْقَلِبُونَ', transliteration: 'Bismillah, alhamdulillah, subhanal-ladhi sakh-khara lana hadha wa ma kunna lahu muqrinin, wa inna ila rabbina lamunqalibun.', translation: 'Im Namen Allahs, alles Lob gebührt Allah. Gepriesen sei Der, Der uns dies dienstbar gemacht hat, und wir wären dazu nicht in der Lage gewesen. Und wahrlich, zu unserem Herrn werden wir zurückkehren.' }] },
        { id: 67, title: 'Gebet für die Reise', duas: [{ arabic: 'اللَّهُ أَكْبَرُ، اللَّهُ أَكْبَرُ، اللَّهُ أَكْبَرُ، سُبْحَانَ الَّذِي سَخَّرَ لَنَا هَذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ، وَإِنَّا إِلَى رَبِّنَا لَمُنْقَلِبُونَ. اللَّهُمَّ إِنَّا نَسْأَلُكَ فِي سَفَرِنَا هَذَا الْبِرَّ وَالتَّقْوَى، وَمِنَ الْعَمَلِ مَا تَرْضَى، اللَّهُمَّ هَوِّنْ عَلَيْنَا سَفَرَنَا هَذَا وَاطْوِ عَنَّا بُعْدَهُ، اللَّهُمَّ أَنْتَ الصَّاحِبُ فِي السَّفَرِ، وَالْخَلِيفَةُ فِي الْأَهْلِ، اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ وَعْثَاءِ السَّفَرِ، وَكَآبَةِ الْمَنْظَرِ، وَسُوءِ الْمُنْقَلَبِ فِي الْمَالِ وَالْأَهْلِ', transliteration: 'Allahu Akbar, Allahu Akbar, Allahu Akbar, Subhanal-ladhi sakh-khara lana hadha wa ma kunna lahu muqrinin, wa inna ila Rabbina lamunqalibun. Allahumma inna nas`aluka fi safarina hadhal-birra wat-taqwa, wa minal-`amali ma tarda, Allahumma hawwin `alayna safarana hadha watwi `anna bu`dah, Allahumma antas-sahibu fis-safar, wal-khalifatu fil-ahl, Allahumma inni a`udhu bika min wa`tha`is-safar, wa kaabatil-manzar, wa su`il-munqalabi fil-mali wal-ahl.', translation: 'Allah ist der Größte... O Allah, wir bitten Dich auf dieser unserer Reise um Rechtschaffenheit und Gottesfurcht und um Taten, die Dir gefallen. O Allah, erleichtere uns diese unsere Reise und mache ihre Entfernung für uns kurz. O Allah, Du bist der Gefährte auf der Reise und der Hüter der Familie. O Allah, ich suche Zuflucht bei Dir vor den Mühsalen der Reise, vor einem traurigen Anblick und vor einer schlimmen Heimkehr in Besitz und Familie.' }] },
        { id: 68, title: 'Beim Betreten eines Dorfes oder einer Stadt', duas: [{ arabic: 'اللَّهُمَّ رَبَّ السَّمَاوَاتِ السَّبْعِ وَمَا أَظْلَلْنَ، وَرَبَّ الْأَرَضِينَ السَّبْعِ وَمَا أَقْلَلْنَ، وَرَبَّ الشَّيَاطِينِ وَمَا أَضْلَلْنَ، وَرَبَّ الرِّيَاحِ وَمَا ذَرَيْنَ، أَسْأَلُكَ خَيْرَ هَذِهِ الْقَرْيَةِ وَخَيْرَ أَهْلِهَا وَخَيْرَ مَا فِيهَا، وَأَعُوذُ بِكَ مِنْ شَرِّهَا وَشَرِّ أَهْلِهَا وَشَرِّ مَا فِيهَا', transliteration: 'Allahumma rabbas-samawatis-sab`i wa ma azlaln, wa rabbal-aradinas-sab`i wa ma aqlaln, wa rabbash-shayatin wa ma adlaln, wa rabbar-riyahi wa ma dharayn, as`aluka khayra hadhihil-qaryah, wa khayra ahliha, wa khayra ma fiha, wa a`udhu bika min sharriha wa sharri ahliha, wa sharri ma fiha.', translation: 'O Allah, Herr der sieben Himmel und dessen, was sie beschatten, Herr der sieben Erden und dessen, was sie tragen, Herr der Satane und dessen, was sie in die Irre führen, und Herr der Winde und dessen, was sie verwehen. Ich bitte Dich um das Gute dieses Ortes, das Gute seiner Bewohner und das Gute, das in ihm ist. Und ich suche Zuflucht bei Dir vor seinem Übel, dem Übel seiner Bewohner und dem Übel, das in ihm ist.' }] },
        { id: 69, title: 'Beim Betreten eines Marktes', duas: [{ arabic: 'لَا إِلَهَ إِلَّا اللَّهُ وَحْدَهُ لَا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، يُحْيِي وَيُمِيتُ، وَهُوَ حَيٌّ لَا يَمُوتُ، بِيَدِهِ الْخَيْرُ، وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', transliteration: 'La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, yuhyi wa yumit, wa huwa hayyun la yamut, bi-yadihil-khayr, wa huwa `ala kulli shay`in qadir.', translation: 'Es gibt keine Gottheit außer Allah allein, ohne Partner. Ihm gehört die Herrschaft und Ihm gehört alles Lob. Er gibt Leben und lässt sterben, und Er ist lebendig und stirbt nicht. In Seiner Hand ist alles Gute, und Er hat Macht über alle Dinge.' }] },
        { id: 70, title: 'Wenn man in einem Haus Rast macht', duas: [{ arabic: 'أَعُوذُ بِكَلِمَاتِ اللهِ التَّامَّاتِ مِنْ شَرِّ مَا خَلَقَ', transliteration: 'A`udhu bikalimatillahit-tammati min sharri ma khalaq.', translation: 'Ich suche Zuflucht bei den vollkommenen Worten Allahs vor dem Übel dessen, was Er erschaffen hat.' }] },
        { id: 71, title: 'Bei der Rückkehr von einer Reise', duas: [{ arabic: 'آيِبُونَ، تَائِبُونَ، عَابِدُونَ، لِرَبِّنَا حَامِدُونَ', transliteration: 'A\'ibuna, ta\'ibuna, `abiduna, li-rabbina hamidun.', translation: 'Wir kehren zurück, bereuen, beten an und preisen unseren Herrn.' }] },
        { id: 72, title: 'Was man sagt, wenn man zufrieden oder unzufrieden ist', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ عَلَى كُلِّ حَالٍ', transliteration: 'Alhamdulillah `ala kulli hal.', translation: 'Alles Lob gebührt Allah unter allen Umständen.' }] },
        { id: 73, title: 'Segenswünsche für den Propheten (ﷺ)', duas: [{ arabic: 'اللَّهُمَّ صَلِّ وَسَلِّمْ عَلَى نَبِيِّنَا مُحَمَّدٍ', transliteration: 'Allahumma salli wa sallim `ala nabiyyina Muhammad.', translation: 'O Allah, sende Deine Gebete und Deinen Frieden auf unseren Propheten Muhammad.' }] },
        { id: 74, title: 'Die Vorzüge des Friedensgrußes', duas: [{ arabic: 'أَفْشُوا السَّلَامَ بَيْنَكُمْ', transliteration: 'Afshus-salama baynakum.', translation: 'Verbreitet den Frieden unter euch.' }] },
        { id: 75, title: 'Wie man den Friedensgruß eines Nicht-Muslims erwidert', duas: [{ arabic: 'وَعَلَيْكُمْ', transliteration: 'Wa `alaykum.', translation: 'Und auf euch.' }] },
        { id: 76, title: 'Beim Hören des Hahnenkrähens', duas: [{ arabic: 'إِذَا سَمِعْتُمْ صِيَاحَ الدِّيَكَةِ فَاسْأَلُوا اللَّهَ مِنْ فَضْلِهِ، فَإِنَّهَا رَأَتْ مَلَكًا', transliteration: 'Idha sami`tum siyaha-d-dikati fas`alul-laha min fadlihi, fa\'innaha ra`at malakan.', translation: 'Wenn ihr das Krähen des Hahnes hört, bittet Allah um Seine Gnade, denn er hat einen Engel gesehen.' }] },
        { id: 77, title: 'Beim Hören des Eselsgeschreis', duas: [{ arabic: 'إِذَا سَمِعْتُمْ نَهِيقَ الْحِمَارِ فَتَعَوَّذُوا بِاللَّهِ مِنَ الشَّيْطَانِ، فَإِنَّهُ رَأَى شَيْطَانًا', transliteration: 'Idha sami`tum nahiqal-himari fata`awwadhu billahi minash-shaytan, fa\'innahu ra`a shaytanan.', translation: 'Wenn ihr das Geschrei des Esels hört, sucht Zuflucht bei Allah vor dem Satan, denn er hat einen Satan gesehen.' }] },
        { id: 78, title: 'Beim Hören des Hundegebells in der Nacht', duas: [{ arabic: 'إِذَا سَمِعْتُمْ نُبَاحَ الْكِلَابِ وَنَهِيقَ الْحَمِيرِ بِاللَّيْلِ، فَتَعَوَّذُوا بِاللَّهِ مِنْهُنَّ، فَإِنَّهُنَّ يَرَيْنَ مَا لَا تَرَوْنَ', transliteration: 'Idha sami`tum nubaha-l-kilabi wa nahiqal-hamiri bil-layl, fata`awwadhu billahi minhunn, fa\'innahunna yarayna ma la tarawn.', translation: 'Wenn ihr in der Nacht das Bellen von Hunden und das Schreien von Eseln hört, sucht Zuflucht bei Allah vor ihnen, denn sie sehen, was ihr nicht seht.' }] },
        { id: 79, title: 'Gebet für jemanden, den man beleidigt hat', duas: [{ arabic: 'اللَّهُمَّ فَأَيُّمَا مُؤْمِنٍ سَبَبْتُهُ فَاجْعَلْ ذَلِكَ لَهُ قُرْبَةً إِلَيْكَ يَوْمَ الْقِيَامَةِ', transliteration: 'Allahumma fa\'ayyuma mu\'minin sababtuhu faj`al dhalika lahu qurbatan ilayka yawmal-qiyamah.', translation: 'O Allah, welchen Gläubigen auch immer ich beleidigt habe, mache dies zu einer Annäherung an Dich am Tag der Auferstehung.' }] },
        { id: 80, title: 'Was ein Muslim sagt, wenn er gelobt wird', duas: [{ arabic: 'اللَّهُمَّ لَا تُؤَاخِذْْنِي بِمَا يَقُولُونَ، وَاغْفِرْ لِي مَا لَا يَعْلَمُونَ، وَاجْعَلْنِي خَيْرًا مِمَّا يَظُنُّونَ', transliteration: 'Allahumma la tu\'akhidhni bima yaqulun, waghfir li ma la ya`lamun, waj`alni khayran mimma yazunnun.', translation: 'O Allah, belange mich nicht für das, was sie sagen, und vergib mir, was sie nicht wissen, und mache mich besser, als sie denken.' }] },
        { id: 81, title: 'Beim Anblick einer frohen Botschaft', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي بِنِعْمَتِهِ تَتِمُّ الصَّالِحَاتُ', transliteration: 'Alhamdulillahi-lladhi bini`matihi tatimmus-salihat.', translation: 'Alles Lob gebührt Allah, durch dessen Gnade gute Taten vollendet werden.' }] },
        { id: 82, title: 'Was man sagt, wenn man etwas sieht, das einem gefällt', duas: [{ arabic: 'مَا شَاءَ اللَّهُ لَا قُوَّةَ إِلَّا بِاللَّهِ', transliteration: 'Ma sha\' Allah, la quwwata illa billah.', translation: 'Was Allah will (geschieht). Es gibt keine Macht außer bei Allah.' }] },
        { id: 83, title: 'Was man sagt, wenn man Schmerzen im Körper spürt', duas: [{ arabic: 'ضَعْ يَدَكَ عَلَى الَّذِي تَأَلَّمَ مِنْ جَسَدِكَ وَقُلْ: بِسْمِ اللَّهِ (ثَلَاثًا) وَقُلْ (سَبْعَ mَرَّاتٍ): أَعُوذُ بِاللَّهِ وَقُدْرَتِهِ مِنْ شَرِّ مَا أَجِدُ وَأُحَاذِرُ', transliteration: 'Lege deine Hand auf die schmerzende Stelle deines Körpers und sage dreimal "Bismillah" und dann siebenmal: "A`udhu billahi wa qudratihi min sharri ma ajidu wa uhadhir".', translation: 'Lege deine Hand auf den Teil deines Körpers, der schmerzt, und sage: "Im Namen Allahs" (dreimal). Dann sage siebenmal: "Ich suche Zuflucht bei Allah und Seiner Macht vor dem Übel, das ich finde und fürchte".' }] },
        { id: 84, title: 'Aus Angst, jemanden mit dem bösen Blick zu treffen', duas: [{ arabic: 'إِذَا رَأَى أَحَدُكُمْ مِنْ أَخِيهِ أَوْ مِنْ نَفْسِهِ أَوْ مِنْ مَالِهِ مَا يُعْجِبُهُ فَلْيُبَرِّكْهُ فَإِنَّ الْعَيْنَ حَقٌّ', transliteration: 'Wenn jemand von euch etwas an seinem Bruder, sich selbst oder seinem Besitz sieht, das ihm gefällt, soll er dafür um Segen beten (barakah), denn der böse Blick ist wahr.', translation: 'Wenn einer von euch etwas an seinem Bruder, an sich selbst oder an seinem Besitz sieht, das ihm gefällt, soll er um Segen dafür bitten, denn der böse Blick ist eine Realität.' }] },
        { id: 85, title: 'Was man sagt, wenn man erschrickt', duas: [{ arabic: 'لَا إِلَهَ إِلَّا اللَّهُ', transliteration: 'La ilaha illallah.', translation: 'Es gibt keine Gottheit außer Allah.' }] },
        { id: 86, title: 'Beim Schlachten oder Opfern', duas: [{ arabic: 'بِسْمِ اللَّهِ وَاللَّهُ أَكْبَرُ [اللَّهُمَّ مِنْكَ وَلَكَ] اللَّهُمَّ تَقَبَّلْ مِنِّي', transliteration: 'Bismillahi wallahu akbar. [Allahumma minka wa lak] Allahumma taqabbal minni.', translation: 'Im Namen Allahs, Allah ist der Größte. [O Allah, von Dir und für Dich]. O Allah, nimm von mir an.' }] },
        { id: 87, title: 'Um die List böser Teufel abzuwehren', duas: [{ arabic: 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّاتِ الَّتِي لَا يُجَاوِزُهُنَّ بَرٌّ وَلَا فَاجِرٌ مِنْ شَرِّ مَا خَلَقَ، وَبَرَأَ وَذَرَأَ... الخ', transliteration: 'A`udhu bikalimatil-lahit-tammati-llati la yujawizuhunna barrun wa la fajirun min sharri ma khalaq, wa bara\'a wa dhara\'...', translation: 'Ich suche Zuflucht bei den vollkommenen Worten Allahs, die weder ein Rechtschaffener noch ein Sünder überschreiten kann, vor dem Übel dessen, was Er erschaffen, hervorgebracht und vermehrt hat...' }] },
        { id: 88, title: 'Um Vergebung bitten und bereuen', duas: [{ arabic: 'أَسْتَغْفِرُ اللَّهَ وَأَتُوبُ إِلَيْهِ', transliteration: 'Astaghfirul-laha wa atubu ilayh.', translation: 'Ich bitte Allah um Vergebung und bereue vor Ihm.' }] },
        { id: 89, title: 'Die Vorzüge von SubhanAllah, Alhamdulillah, La ilaha illallah, Allahu Akbar', duas: [{ arabic: 'أَحَبُّ الْكَلَامِ إِلَى اللَّهِ أَرْبَعٌ: سُبْحَانَ اللَّهِ، وَالْحَمْدُ لِلَّهِ، وَلَا إِلَهَ إِلَّا اللَّهُ، وَاللَّهُ أَكْبَرُ', transliteration: 'Die liebsten Worte bei Allah sind vier: Subhan-Allah, wal-Hamdu lillah, wa la ilaha illallah, wallahu akbar.', translation: 'Die beliebtesten Worte bei Allah sind vier: "Gepriesen sei Allah", "Alles Lob gebührt Allah", "Es gibt keine Gottheit außer Allah", "Allah ist der Größte".' }] },
        { id: 90, title: 'Wie der Prophet (ﷺ) Tasbih gemacht hat', duas: [{ arabic: 'كَانَ يَعْقِدُ التَّسْبِيحَ بِيَمِينِهِ', transliteration: 'Er zählte den Tasbih an seiner rechten Hand.', translation: 'Er pflegte den Tasbih (die Lobpreisung) mit seiner rechten Hand zu zählen.' }] },
        { id: 91, 'title': 'Die umfassende Natur des Guten', 'duas': [{'arabic': 'كُلُّ مَعْرُوفٍ صَدَقَةٌ', 'transliteration': 'Kullu ma`rufin sadaqah.', 'translation': 'Jede gute Tat ist eine Sadaqa (Wohltätigkeit).'}]},
        { id: 92, 'title': 'Bittgebet für jemanden, der dir einen Gefallen getan hat', 'duas': [{'arabic': 'جَزَاكَ اللَّهُ خَيْرًا', 'transliteration': 'Jazakallahu khayran.', 'translation': 'Möge Allah dich mit Gutem belohnen.'}]},
        { id: 93, 'title': 'Schutz vor dem Dajjal', 'duas': [{'arabic': 'حِفْظُ عَشْرِ آيَاتٍ مِنْ أَوَّلِ سُورَةِ الْكَهْفِ', 'transliteration': 'Hifzhu `ashri ayatin min awwali suratil-kahf.', 'translation': 'Das Auswendiglernen der ersten zehn Verse der Sure Al-Kahf.'}]},
        { id: 94, 'title': 'Gebet für jemanden, der sagt "Ich liebe dich für Allahs willen"', 'duas': [{'arabic': 'أَحَبَّكَ الَّذِي أَحْبَبْتَنِي لَهُ', 'transliteration': 'Ahabbakal-ladhi ahbabtani lah.', 'translation': 'Möge Der dich lieben, um dessentwillen du mich liebst.'}]},
        { id: 95, 'title': 'Gebet für jemanden, der dir finanziell geholfen hat', 'duas': [{'arabic': 'بَارَكَ اللَّهُ لَكَ فِي أَهْلِكَ وَمَالِكَ', 'transliteration': 'Barakallahu laka fi ahlika wa malik.', 'translation': 'Möge Allah dich in deiner Familie und deinem Vermögen segnen.'}]},
        { id: 96, 'title': 'Gebet bei der Rückzahlung einer Schuld', 'duas': [{'arabic': 'بَارَكَ اللَّهُ لَكَ فِي أَهْلِكَ وَمَالِكَ، إِنَّمَا جَزَاءُ السَّلَفِ الْحَمْدُ وَالْأَدَاءُ', 'transliteration': 'Barakallahu laka fi ahlika wa malik, innama jaza`us-salafil-hamdu wal-ada\'.', 'translation': 'Möge Allah dich in deiner Familie und deinem Vermögen segnen. Wahrlich, die Belohnung für ein Darlehen ist Lob und die Rückzahlung.'}]},
        { id: 97, 'title': 'Gebet gegen Aberglauben', 'duas': [{'arabic': 'اللَّهُمَّ لَا طَيْرَ إِلَّا طَيْرُكَ، وَلَا خَيْرَ إِلَّا خَيْرُكَ، وَلَا إِلَهَ غَيْرُكَ', 'transliteration': 'Allahumma la tayra illa tayruk, wa la khayra illa khayruk, wa la ilaha ghayruk.', 'translation': 'O Allah, es gibt kein Omen außer Deinem Omen, und kein Gutes außer Deinem Guten, und es gibt keine Gottheit außer Dir.'}]},
        { id: 98, 'title': 'Gebet beim Besteigen eines Reittiers/Fahrzeugs', 'duas': [{'arabic': 'بِسْمِ اللَّهِ، الْحَمْدُ لِلَّهِ. {sُبْحَانَ الَّذِي sَخَّرَ لَنَا هَذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ * وَإِنَّا إِلَى رَبِّنَا لَمُنْقَلِبُونَ}', 'transliteration': 'Bismillah, alhamdulillah. Subhanal-ladhi sakh-khara lana hadha wa ma kunna lahu muqrinin. Wa inna ila Rabbina lamunqalibun.', 'translation': 'Im Namen Allahs, alles Lob gebührt Allah. Gepriesen sei Der, Der uns dies dienstbar gemacht hat, und wir wären dazu nicht in der Lage gewesen. Und wahrlich, zu unserem Herrn werden wir zurückkehren.'}]},
        { id: 99, 'title': 'Gebet für einen frisch Vermählten', 'duas': [{'arabic': 'بَارَكَ اللَّهُ لَكَ وَبَارَكَ عَلَيْكَ وَجَمَعَ بَيْنَكُمَا فِي خَيْرٍ', 'transliteration': 'Barakallahu laka, wa baraka `alayka, wa jama`a baynakuma fi khayr.', 'translation': 'Möge Allah dich segnen, und möge Er Seinen Segen über dich ausgießen, und möge Er euch beide im Guten vereinen.'}]},
        { id: 100, 'title': 'Gebet des frisch Vermählten für sich selbst', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَسْأَلُكَ خَيْرَهَا وَخَيْرَ مَا جَبَلْتَهَا عَلَيْهِ، وَأَعُوذُ بِكَ مِنْ شَرِّهَا وَشَرِّ مَا جَبَلْتَهَا عَلَيْهِ', 'transliteration': 'Allahumma inni as`aluka khayraha wa khayra ma jabaltaha `alayh, wa a`udhu bika min sharriha wa sharri ma jabaltaha `alayh.', 'translation': 'O Allah, ich bitte Dich um ihr Gutes und das Gute, zu dem Du sie veranlagt hast, und ich suche Zuflucht bei Dir vor ihrem Schlechten und dem Schlechten, zu dem Du sie veranlagt hast.'}]},
        { id: 101, 'title': 'Gebet vor dem ehelichen Verkehr', 'duas': [{'arabic': 'بِسْمِ اللَّهِ، اللَّهُمَّ جَنِّبْنَا الشَّيْطَانَ، وَجَنِّبِ الشَّيْطَانَ مَا رَزَقْتَنَا', 'transliteration': 'Bismillah, Allahumma jannibnash-shaytan, wa jannibish-shaytana ma razaqtana.', 'translation': 'Im Namen Allahs. O Allah, halte den Satan von uns fern und halte den Satan fern von dem, was Du uns schenkst.'}]},
        { id: 102, 'title': 'Beim Zornigwerden', 'duas': [{'arabic': 'أَعُوذُ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ', 'transliteration': 'A`udhu billahi minash-shaytanir-rajim.', 'translation': 'Ich suche Zuflucht bei Allah vor dem verfluchten Satan.'}]},
        { id: 103, 'title': 'Sühne für eine Sitzung (Kaffaratul-Majlis)', 'duas': [{'arabic': 'sُبْحَانَكَ اللَّهُمَّ وَبِحَمْدِكَ، أَشْهَدُ أَنْ لَا إِلَهَ إِلَّا أَنْتَ، أَسْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', 'transliteration': 'Subhanakallahumma wa bihamdik, ashhadu an la ilaha illa ant, astaghfiruka wa atubu ilayk.', 'translation': 'Gepriesen seist Du, o Allah, und mit Deinem Lob. Ich bezeuge, dass es keine Gottheit gibt außer Dir. Ich bitte Dich um Vergebung und bereue vor Dir.'}]},
        { id: 104, 'title': 'Gebet für jemanden, der sagt "Ghafarallahu lak"', 'duas': [{'arabic': 'وَلَكَ', 'transliteration': 'Wa lak.', 'translation': 'Und dir auch.'}]},
        { id: 105, 'title': 'Was man sagt, wenn es regnet', 'duas': [{'arabic': 'اللَّهُمَّ صَيِّبًا نَافِعًا', 'transliteration': 'Allahumma sayyiban nafi`a.', 'translation': 'O Allah, (mache es zu) einem nützlichen Regen.'}]},
        { id: 106, 'title': 'Nach dem Regen', 'duas': [{'arabic': 'مُطِرْنَا بِفَضْلِ اللَّهِ وَرَحْمَتِهِ', 'transliteration': 'Mutirna bi-fadlillahi wa rahmatih.', 'translation': 'Wir wurden durch Allahs Gnade und Barmherzigkeit mit Regen beschenkt.'}]},
        { id: 107, 'title': 'Bitte um klaren Himmel', 'duas': [{'arabic': 'اللَّهُمَّ حَوَالَيْنَا وَلَا عَلَيْنَا، اللَّهُمَّ عَلَى الْآكَامِ وَالظِّرَابِ، وَبُطُونِ الْأَوْدِيَةِ، وَمَنَابِتِ الشَّجَرِ', 'transliteration': 'Allahumma hawalayna wa la `alayna, allahumma `alal-akami waz-zirab, wa butunil-awdiyah, wa manabitish-shajar.', 'translation': 'O Allah, um uns herum und nicht auf uns. O Allah, auf die Hügel und Berge, in die Täler und auf die Pflanzenwurzeln.'}]},
        { id: 108, 'title': 'Was man sagt, wenn man Angst vor etwas hat', 'duas': [{'arabic': 'اللَّهُمَّ اكْفِنِيهِمْ بِمَا شِئْتَ', 'transliteration': 'Allahumma-kfinihim bima shi`t.', 'translation': 'O Allah, genüge mir gegen sie, wie immer Du willst.'}]},
        { id: 109, 'title': 'Was man sagt, wenn man etwas Unerwünschtes sieht', 'duas': [{'arabic': 'قَدَرُ اللَّهِ وَمَا شَاءَ فَعَلَ', 'transliteration': 'Qadarul-lahi wa ma sha\'a fa`al.', 'translation': 'Es ist das Schicksal Allahs, und was Er will, tut Er.'}]},
        { id: 110, 'title': 'Trost für die Hinterbliebenen', 'duas': [{'arabic': 'إِنَّ لِلَّهِ مَا أَخَذَ، وَلَهُ مَا أَعْطَى، وَكُلُّ شَيْءٍ عِنْدَهُ بِأَجَلٍ مُsَمًّى... فَلْتَصْبِرْ وَلْتَحْتَسِبْ', 'transliteration': 'Inna lillahi ma akhadh, wa lahu ma a`ta, wa kullu shay`in `indahu bi-ajalin musamma... faltasbir waltahtasib.', 'translation': 'Wahrlich, Allah gehört, was Er genommen hat, und Ihm gehört, was Er gegeben hat, und alles bei Ihm hat eine festgesetzte Frist... So sei geduldig und hoffe auf Belohnung.'}]},
        { id: 111, 'title': 'Wenn man das Grab besucht', 'duas': [{'arabic': 'السَّلَامُ عَلَى أَهْلِ الدِّيَارِ مِنَ الْمُؤْمِنِينَ وَالْمُsْلِمِينَ، وَيَرْحَمُ اللَّهُ الْمُsْتَقْدِمِينَ مِنَّا وَالْمُsْتَأْخِرِينَ، وَإِنَّا إِنْ شَاءَ اللَّهُ بِكُمْ لَلَاحِقُونَ', 'transliteration': 'As-salamu `ala ahlid-diyari minal-mu`minina wal-muslimin, wa yarhamul-lahul-mustaqdimina minna wal-musta`khirin, wa inna in sha\'al-lahu bikum lalahiqun.', 'translation': 'Friede sei mit den Bewohnern der Wohnstätten, den Gläubigen und Muslimen. Möge Allah sich derer von uns erbarmen, die vorausgegangen sind, und derer, die nachfolgen. Und wir werden, so Allah will, euch folgen.'}]},
        { id: 112, 'title': 'Beim Hören des Donners', 'duas': [{'arabic': 'sُبْحَانَ الَّذِي يُsَبِّحُ الرَّعْدُ بِحَمْدِهِ وَالْمَلَائِكَةُ مِنْ خِيفَتِهِ', 'transliteration': 'Subhanal-ladhi yusabbihur-ra`du bi-hamdihi wal-mala\'ikatu min khifatih.', 'translation': 'Gepriesen sei Der, Dessen Lob der Donner preist und auch die Engel aus Furcht vor Ihm.'}]},
        { id: 113, 'title': 'Gebet um Regen', 'duas': [{'arabic': 'اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا، اللَّهُمَّ أَغِثْنَا', 'transliteration': 'Allahumma aghithna, Allahumma aghithna, Allahumma aghithna.', 'translation': 'O Allah, sende uns Regen. O Allah, sende uns Regen. O Allah, sende uns Regen.'}]},
        { id: 114, 'title': 'Wenn man sich selbst im Spiegel sieht', 'duas': [{'arabic': 'اللَّهُمَّ كَمَا حَسَّنْتَ خَلْقِي فَحَسِّنْ خُلُقِي', 'transliteration': 'Allahumma kama hassanta khalqi fahassin khuluqi.', 'translation': 'O Allah, so wie Du meine Gestalt schön gemacht hast, so mache auch meinen Charakter schön.'}]},
        { id: 115, 'title': 'Vor dem Lesen des Korans', 'duas': [{'arabic': 'أَعُوذُ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ', 'transliteration': 'A`udhu billahi minash-shaytanir-rajim.', 'translation': 'Ich suche Zuflucht bei Allah vor dem verfluchten Satan.'}]},
        { id: 116, 'title': 'Nach Beendigung einer Versammlung', 'duas': [{'arabic': 'sُبْحَانَكَ اللَّهُمَّ وَبِحَمْدِكَ، أَشْهَدُ أَنْ لَا إِلَهَ إِلَّا أَنْتَ، أَsْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', 'transliteration': 'Subhanakallahumma wa bihamdik, ashhadu an la ilaha illa ant, astaghfiruka wa atubu ilayk.', 'translation': 'Gepriesen seist Du, o Allah, und mit Deinem Lob. Ich bezeuge, dass es keine Gottheit gibt außer Dir. Ich bitte Dich um Vergebung und bereue vor Dir.'}]},
        { id: 117, 'title': 'Wenn man auf einem Markt ist', 'duas': [{'arabic': 'لَا إِلَهَ إِلَّا اللَّهُ وَحْدَهُ لَا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، يُحْyِي وَيُمِيتُ وَهُوَ حَيٌّ لَا يَمُوتُ، بِيَدِهِ الْخَيْرُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'transliteration': 'La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, yuhyi wa yumit, wa huwa hayyun la yamut, bi-yadihil-khayr, wa huwa `ala kulli shay\'in qadir.', 'translation': 'Es gibt keine Gottheit außer Allah allein, ohne Partner. Ihm gehört die Herrschaft und Ihm gehört alles Lob. Er gibt Leben und lässt sterben, und Er ist lebendig und stirbt nicht. In Seiner Hand ist alles Gute, und Er ist über alle Dinge allmächtig.'}]},
        { id: 118, 'title': 'Für den, der sagt: "Ich liebe dich um Allahs willen"', 'duas': [{'arabic': 'أَحَبَّكَ الَّذِي أَحْبَبْتَنِي لَهُ', 'transliteration': 'Ahabbakal-ladhi ahbabtani lah.', 'translation': 'Möge Derjenige dich lieben, um dessentwillen du mich liebst.'}]},
        { id: 119, 'title': 'Wenn man ein Geschenk erhält', 'duas': [{'arabic': 'جَزَاكَ اللَّهُ خَيْرًا', 'transliteration': 'Jazakallahu khayra.', 'translation': 'Möge Allah dich mit Gutem belohnen.'}]},
        { id: 120, 'title': 'Wenn man niest', 'duas': [{'arabic': 'إِذَا عَطَسَ أَحَدُكُمْ فَلْيَقُلْ: الْحَمْدُ لِلَّهِ. وَلْيَقُلْ لَهُ أَخُوهُ: يَرْحَمُكَ اللَّهُ. فَإِذَا قَالَ لَهُ: يَرْحَمُكَ اللَّهُ، فَلْيَقُلْ: يَهْدِيكُمُ اللَّهُ وَيُصْلِحُ بَالَكُمْ', 'transliteration': 'Wenn einer von euch niest, soll er sagen: "Alhamdulillah". Sein Bruder soll zu ihm sagen: "Yarhamukallah". Und er soll antworten: "Yahdikumullahu wa yuslihu balakum".', 'translation': 'Wenn einer von euch niest, soll er "Alhamdulillah" (Alles Lob gebührt Allah) sagen. Sein Bruder soll zu ihm sagen "Yarhamukallah" (Möge Allah dir barmherzig sein). Daraufhin soll er antworten "Yahdikumullahu wa yuslihu balakum" (Möge Allah euch rechtleiten und euren Zustand verbessern).'}]},
        { id: 121, 'title': 'Wenn man nachts aufwacht', 'duas': [{'arabic': 'لَا إِلَهَ إِلَّا اللَّهُ وَحْدَهُ لَا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ. الْحَمْدُ لِلَّهِ، وَsُبْحَانَ اللَّهِ، وَلَا إِلَهَ إِلَّا اللَّهُ، وَاللَّهُ أَكْبَرُ، وَلَا حَوْلَ وَلَا قُوَّةَ إِلَّا بِاللَّهِ', 'transliteration': 'La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, wa huwa `ala kulli shay\'in qadir. Alhamdulillah, wa subhan-Allah, wa la ilaha illallah, wallahu akbar, wa la hawla wa la quwwata illa billah.', 'translation': 'Es gibt keine Gottheit außer Allah allein, ohne Partner. Ihm gehört die Herrschaft und Ihm gehört alles Lob, und Er ist über alle Dinge allmächtig. Alles Lob gebührt Allah, gepriesen sei Allah, es gibt keine Gottheit außer Allah, Allah ist der Größte, und es gibt keine Macht und keine Kraft außer bei Allah.'}]},
        { id: 122, 'title': 'Bei einem schlechten Traum', 'duas': [{'arabic': 'يَنْفُثُ عَنْ يَsَارِهِ (ثَلَاثًا). يَsْتَعِيذُ بِاللَّهِ مِنَ الشَّيْطَانِ وَمِنْ شَرِّ مَا رَأَى (ثَلَاثَ مَرَّاتٍ). لَا يُحَدِّثْ بِهَا أَحَدًا. يَتَحَوَّلُ عَنْ جَنْبِهِ الَّذِي كَانَ عَلَيْهِ.', 'transliteration': 'Leicht nach links spucken (3x). Zuflucht bei Allah vor dem Satan und vor dem Übel dessen suchen, was man gesehen hat (3x). Niemandem davon erzählen. Sich auf die andere Seite legen.', 'translation': 'Spit lightly to your left (3 times). Seek refuge with Allah from Satan and from the evil of what you have seen (3 times). Do not tell anyone about it. Turn over to your other side.'}]},
        { id: 123, 'title': 'Die Vorzüglichkeit des Gedenkens an Allah', 'duas': [{'arabic': 'مَثَلُ الَّذِي يَذْكُرُ رَبَّهُ وَالَّذِي لَا يَذْكُرُ رَبَّهُ مَثَلُ الْحَيِّ وَالْمَيِّتِ', 'transliteration': 'Mathalul-ladhi yadhkuru rabbahu walladhi la yadhkuru rabbahu mathalul-hayyi wal-mayyit.', 'translation': 'Das Gleichnis desjenigen, der seines Herrn gedenkt, und desjenigen, der seines Herrn nicht gedenkt, ist wie das Gleichnis des Lebenden und des Toten.'}]},
        { id: 124, 'title': 'Gebet um Rechtleitung', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ الْهُدَى وَالتُّقَى وَالْعَفَافَ وَالْغِنَى', 'transliteration': 'Allahumma inni as\'alukal-huda wat-tuqa wal-`afafa wal-ghina.', 'translation': 'O Allah, ich bitte Dich um Rechtleitung, Gottesfurcht, Enthaltsamkeit und Reichtum.'}]},
        { id: 125, 'title': 'Gebet um nützliches Wissen', 'duas': [{'arabic': 'اللَّهُمَّ انْفَعْنِي بِمَا عَلَّمْتَنِي، وَعَلِّمْنِي مَا يَنْفَعُنِي، وَزِدْنِي عِلْمًا', 'transliteration': 'Allahumma-nfa`ni bima `allamtani, wa `allimni ma yanfa`uni, wa zidni `ilma.', 'translation': 'O Allah, lass mich von dem profitieren, was Du mich gelehrt hast, lehre mich, was mir nützt, und mehre mein Wissen.'}]},
        { id: 126, 'title': 'Gebet um einen guten Charakter', 'duas': [{'arabic': 'اللَّهُمَّ اهْدِنِي لِأَحْسَنِ الْأَخْلَاقِ لَا يَهْدِي لِأَحْسَنِهَا إِلَّا أَنْتَ، وَاصْرِفْ عَنِّي sَيِّئَهَا لَا يَصْرِفُ عَنِّي sَيِّئَهَا إِلَّا أَنْتَ', 'transliteration': 'Allahumma-hdini li-ahsanil-akhlaq, la yahdi li-ahsaniha illa ant, wasrif `anni sayyi\'aha, la yasrifu `anni sayyi\'aha illa ant.', 'translation': 'O Allah, leite mich zum besten Charakter, denn niemand leitet zum besten davon außer Dir. Und wende den schlechten Charakter von mir ab, denn niemand wendet seinen schlechten Charakter von mir ab außer Dir.'}]},
        { id: 127, 'title': 'Gebet für die Eltern', 'duas': [{'arabic': 'رَبِّ ارْحَمْهُمَا كَمَا رَبَّيَانِي صَغِيرًا', 'transliteration': 'Rabbir-hamhuma kama rabbayani saghira.', 'translation': 'Mein Herr, erbarme Dich ihrer, wie sie mich als kleines Kind aufgezogen haben.'}]},
        { id: 128, 'title': 'Gebet für das Gute im Dies- und Jenseits', 'duas': [{'arabic': 'رَبَّنَا آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ', 'transliteration': 'Rabbana atina fid-dunya hasanatan wa fil-akhirati hasanatan waqina `adhaban-nar.', 'translation': 'Unser Herr, gib uns im Diesseits Gutes und im Jenseits Gutes und bewahre uns vor der Strafe des Feuers.'}]},
        { id: 129, 'title': 'Gebet um Standhaftigkeit im Glauben', 'duas': [{'arabic': 'يَا مُقَلِّبَ الْقُلُوبِ ثَبِّتْ قَلْبِي عَلَى دِينِكَ', 'transliteration': 'Ya muqallibal-qulub, thabbit qalbi `ala dinik.', 'translation': 'O Turner der Herzen, mache mein Herz firm auf Deiner Religion.'}]},
        { id: 130, 'title': 'Schutz vor dem Übel der eigenen Seele', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ شَرِّ نَفْسِي، وَمِنْ شَرِّ كُلِّ دَابَّةٍ أَنْتَ آخِذٌ بِنَاصِيَتِهَا', 'transliteration': 'Allahumma inni a`udhu bika min sharri nafsi, wa min sharri kulli dabbatin anta akhidhun bi-nasiyatiha.', 'translation': 'O Allah, ich suche Zuflucht bei Dir vor dem Übel meiner eigenen Seele und vor dem Übel jedes Geschöpfes, das Du an der Stirnlocke hältst.'}]},
        { id: 131, 'title': 'Meister der Vergebung (Sayyid al-Istighfar)', 'duas': [{'arabic': 'اللَّهُمَّ أَنْتَ رَبِّي لَا إِلَهَ إِلَّا أَنْتَ، خَلَقْتَنِي وَأَنَا عَبْدُكَ... (vollständiges Dua)', 'transliteration': 'Allahumma anta rabbi la ilaha illa ant, khalaqtani wa ana `abduk...', 'translation': 'O Allah, Du bist mein Herr, es gibt keine Gottheit außer Dir, Du hast mich erschaffen und ich bin Dein Diener...'}]},
        { id: 132, 'title': 'Die Vorzüge von Alhamdulillah', 'duas': [{'arabic': 'الْحَمْدُ لِلَّهِ تَمْلَأُ الْمِيزَانَ', 'transliteration': 'Al-hamdu lillah tamla`ul-mizan.', 'translation': 'Alhamdulillah füllt die Waage.'}]},
        { id: 133, 'title': 'Dankbarkeit gegenüber den Menschen', 'duas': [{'arabic': 'لَا يَشْكُرُ اللَّهَ مَنْ لَا يَشْكُرُ النَّاسَ', 'transliteration': 'La yashkurul-laha man la yashkurun-nas.', 'translation': 'Wer den Menschen nicht dankt, dankt auch Allah nicht.'}]},
        { id: 134, 'title': 'Die Wichtigkeit des Wissens', 'duas': [{'arabic': 'طَلَبُ الْعِلْمِ فَرِيضَةٌ عَلَى كُلِّ مُسْلِمٍ', 'transliteration': 'Talabul-`ilmi faridatun `ala kulli muslim.', 'translation': 'Das Streben nach Wissen ist eine Pflicht für jeden Muslim.'}]},
        { id: 135, 'title': 'Das beste Gebet', 'duas': [{'arabic': 'أَفْضَلُ الدُّعَاءِ الْحَمْدُ لِلَّهِ', 'transliteration': 'Afdalud-du`a\'i al-hamdu lillah.', 'translation': 'Das beste Bittgebet ist "Alhamdulillah".'}]},
        { id: 136, 'title': 'Schutz vor Geiz', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنَ الْبُخْلِ', 'transliteration': 'Allahumma inni a`udhu bika minal-bukhl.', 'translation': 'O Allah, ich suche Zuflucht bei Dir vor dem Geiz.'}]},
        { id: 137, 'title': 'Das Paradies betreten durch Barmherzigkeit', 'duas': [{'arabic': 'لَنْ يُدْخِلَ أَحَدًا عَمَلُهُ الْجَنَّةَ... قَالُوا: وَلَا أَنْتَ يَا رَسُولَ اللَّهِ؟ قَالَ: وَلَا أَنَا، إِلَّا أَنْ يَتَغَمَّdَنِي اللَّهُ بِفَضْلٍ وَرَحْمَةٍ', 'transliteration': 'Niemanden wird seine Tat ins Paradies bringen... sie sagten: "Nicht einmal dich, o Gesandter Allahs?" Er sagte: "Nicht einmal mich, es sei denn, Allah umhüllt mich mit Seiner Gnade und Barmherzigkeit."', 'translation': 'Keiner von euch wird aufgrund seiner Taten das Paradies betreten... Sie sagten: "Nicht einmal du, o Gesandter Allahs?" Er sagte: "Nicht einmal ich, es sei denn, Allah umhüllt mich mit Seiner Gnade und Barmherzigkeit."'}]},
        { id: 138, 'title': 'Verbot des Zorns', 'duas': [{'arabic': 'لَا تَغْضَبْ وَلَكَ الْجَنَّةُ', 'transliteration': 'La taghdab wa lakal-jannah.', 'translation': 'Sei nicht zornig, und du wirst das Paradies haben.'}]},
        { id: 139, 'title': 'Die Belohnung für das Aufziehen von Töchtern', 'duas': [{'arabic': 'مَنْ عَالَ جَارِيَتَيْنِ حَتَّى تَبْلُغَا، جَاءَ يَوْمَ الْقِيَامَةِ أَنَا وَهُوَ كَهَاتَيْنِ', 'transliteration': 'Wer zwei Töchter aufzieht, bis sie die Pubertät erreichen, wird am Tag der Auferstehung so (nahe) bei mir sein wie diese beiden (Finger).', 'translation': 'Wer auch immer zwei Töchter unterstützt, bis sie die Reife erreichen, wird am Tag der Qiyamah mit mir so kommen (und er verband seine Finger).'}]},
        { id: 140, 'title': 'Die Einfachheit des Islam', 'duas': [{'arabic': 'إِنَّ الدِّينَ يُsْرٌ', 'transliteration': 'Innad-dina yusrun.', 'translation': 'Wahrlich, die Religion ist einfach.'}]},
        { id: 141, title: 'Bei der Reise', duas: [{ arabic: 'اللَّهُمَّ أَنْتَ الصَّاحِبُ فِي السَّفَرِ، وَالْخَلِيفَةُ فِي الْأَهْلِ', transliteration: 'Allahumma antas-sahibu fis-safar, wal-khalifatu fil-ahl.', translation: 'O Allah, Du bist der Gefährte auf der Reise und der Hüter der Familie.' }] },
        { id: 142, title: 'Beim Sehen eines erfreulichen Traums', duas: [{ arabic: 'يَحْمَدُ اللَّهَ عَلَيْهِ، وَيُحَدِّثُ بِهِ مَنْ يُحِبُّ', transliteration: 'Yahmadul-laha `alayh, wa yuhaddithu bihi man yuhibb.', translation: 'Er soll Allah dafür loben und es denen erzählen, die er liebt.' }] },
        { id: 143, title: 'Beim Betreten des Marktes', duas: [{ arabic: 'لَا إِلَهَ إِلَّا اللَّهُ وَحْدَهُ لَا شَرِيكَ لَهُ، لَهُ الْمُلْكُ، وَلَهُ الْحَمْدُ، يُحْيِي وَيُمِيتُ، وَهُوَ حَيٌّ لَا يَمُوتُ، بِيَدِهِ الْخَيْرُ، وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', transliteration: 'La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, yuhyi wa yumit, wa huwa hayyun la yamut, bi-yadihil-khayr, wa huwa `ala kulli shay`in qadir.', translation: 'Es gibt keine Gottheit außer Allah allein, ohne Partner. Ihm gehört die Herrschaft und Ihm gehört alles Lob. Er gibt Leben und lässt sterben, und Er ist lebendig und stirbt nicht. In Seiner Hand ist alles Gute, und Er hat Macht über alle Dinge.' }] },
        { id: 144, title: 'Beim Aufstieg', duas: [{ arabic: 'اللَّهُ أَكْبَرُ', transliteration: 'Allahu Akbar.', translation: 'Allah ist der Größte.' }] },
        { id: 145, title: 'Beim Abstieg', duas: [{ arabic: 'sُبْحَانَ اللَّهِ', transliteration: 'Subhanallah.', translation: 'Gepriesen sei Allah.' }] },
        { id: 146, title: 'Gebet um Regen', duas: [{ arabic: 'اللَّهُمَّ اسْقِنَا غَيْثًا مُغِيثًا مَرِيئًا مَرِيعًا، نَافِعًا غَيْرَ ضَارٍّ، عَاجِلًا غَيْرَ آجِلٍ', transliteration: 'Allahummas-qina ghaythan mughithan mari`an mari`a, nafi`an ghayra darr, `ajilan ghayra ajil.', translation: 'O Allah, gib uns einen rettenden, bekömmlichen, fruchtbaren, nützlichen und nicht schädlichen Regen, sofort und nicht verzögert.' }] },
        { id: 147, title: 'Was man am Tag von Arafah sagt', duas: [{ arabic: 'خَيْرُ الدُّعَاءِ دُعَاءُ يَوْمِ عَرَفَةَ، وَخَيْرُ مَا قُلْتُ أَنَا وَالنَّبِيُّونَ مِنْ قَبْلِي: لَا إِلَهَ إِلَّا اللَّهُ وَحْدَهُ لَا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', transliteration: 'Khayrud-du`a`i du`a`u yawmi `arafah, wa khayru ma qultu ana wan-nabiyyuna min qabli: La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, wa huwa `ala kulli shay`in qadir.', translation: 'Das beste Gebet ist das Gebet am Tag von Arafah, und das Beste, was ich und die Propheten vor mir gesagt haben, ist: "Es gibt keine Gottheit außer Allah allein, ohne Partner. Ihm gehört die Herrschaft und Ihm gehört alles Lob, und Er hat Macht über alle Dinge."' }] },
        { id: 148, title: 'Beim Trinken von Zamzam-Wasser', duas: [{ arabic: 'مَاءُ زَمْزَمَ لِمَا شُرِبَ لَهُ', transliteration: 'Ma`u zamzama lima shuriba lah.', translation: 'Das Zamzam-Wasser ist für das, wofür es getrunken wird.' }] },
        { id: 149, title: 'Beim Anblick einer Katastrophe bei anderen', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي عَافَانِي مِمَّا ابْتَلَاكَ بِهِ وَفَضَّلَنِي عَلَى كَثِيرٍ مِمَّنْ خَلَقَ تَفْضِيلًا', transliteration: 'Alhamdu lillahil-ladhi `afani mimmab-talaka bih, wa faddalani `ala kathirin mimman khalaqa tafdila.', translation: 'Alles Lob gebührt Allah, der mich vor dem bewahrt hat, womit Er dich geprüft hat, und mich vor vielen Seiner Geschöpfe bevorzugt hat.' }] },
        { id: 150, title: 'Vor dem Geschlechtsverkehr', duas: [{ arabic: 'بِسْمِ اللَّهِ، اللَّهُمَّ جَنِّبْنَا الشَّيْطَانَ وَجَنِّبِ الشَّيْطَانَ مَا رَزَقْتَنَا', transliteration: 'Bismillah, Allahumma jannibnash-shaytan, wa jannibish-shaytana ma razaqtana.', translation: 'Im Namen Allahs. O Allah, halte den Satan von uns fern und halte den Satan fern von dem, was Du uns schenkst.' }] },
        { id: 151, title: 'Beim Eintreten in eine Stadt', duas: [{ arabic: 'اللَّهُمَّ بَارِكْ لَنَا فِيهَا', transliteration: 'Allahumma barik lana fiha.', translation: 'O Allah, segne uns in ihr.' }] },
        { id: 152, title: 'Dankbarkeit für eine Gabe', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي بِنِعْمَتِهِ تَتِمُّ الصَّالِحَاتُ', transliteration: 'Alhamdu lillahil-ladhi bi-ni`matihi tatimmus-salihat.', translation: 'Alles Lob gebührt Allah, durch dessen Gnade gute Taten vollendet werden.' }] },
        { id: 153, title: 'Beim Betrachten im Spiegel', duas: [{ arabic: 'اللَّهُمَّ كَمَا حَسَّنْتَ خَلْقِي فَحَسِّنْ خُلُقِي', transliteration: 'Allahumma kama hassanta khalqi, fahassin khuluqi.', translation: 'O Allah, so wie Du meine Gestalt schön gemacht hast, so mache auch meinen Charakter schön.' }] },
        { id: 154, title: 'Sühne für üble Nachrede', duas: [{ arabic: 'اللَّهُمَّ اغْفِرْ لَنَا وَلَهُ', transliteration: 'Allahummagh-fir lana wa lah.', translation: 'O Allah, vergib uns und ihm.' }] },
        { id: 155, title: 'Beim Hören des Krähens eines Hahns', duas: [{ arabic: 'إِذَا سَمِعْتُمْ صِيَاحَ الدِّيَكَةِ فَاسْأَلُوا اللَّهَ مِنْ فَضْلِهِ، فَإِنَّهَا رَأَتْ مَلَكًا', transliteration: 'Idha sami`tum siyaha-d-dikati fas`alul-laha min fadlihi, fa`innaha ra`at malakan.', translation: 'Wenn ihr das Krähen des Hahnes hört, bittet Allah um Seine Gnade, denn er hat einen Engel gesehen.' }] },
        { id: 156, title: 'Beim Hören des Schreiens eines Esels', duas: [{ arabic: 'إِذَا سَمِعْتُمْ نَهِيقَ الْحِمَارِ فَتَعَوَّذُوا بِاللَّهِ مِنَ الشَّيْطَانِ، فَإِنَّهُ رَأَى شَيْطَانًا', transliteration: 'Idha sami`tum nahiqal-himari fata`awwadhu billahi minash-shaytan, fa`innahu ra`a shaytanan.', translation: 'Wenn ihr das Geschrei des Esels hört, sucht Zuflucht bei Allah vor dem Satan, denn er hat einen Satan gesehen.' }] },
        { id: 157, title: 'Schutz vor dem Feuer', duas: [{ arabic: 'اللَّهُمَّ أَجِرْنِي مِنَ النَّارِ', transliteration: 'Allahumma ajirni minan-nar.', translation: 'O Allah, schütze mich vor dem Feuer.' }] },
        { id: 158, title: 'Beim Abschluss des Korans', duas: [{ arabic: 'اللَّهُمَّ ارْحَمْنِي بِالْقُرْآنِ وَاجْعَلْهُ لِي إِمَامًا وَنُورًا وَهُدًى وَرَحْمَةً', transliteration: 'Allahummar-hamni bil-qur`an, waj`alhu li imaman wa nuran wa hudan wa rahmah.', translation: 'O Allah, habe Barmherzigkeit mit mir durch den Koran und mache ihn zu einem Führer, einem Licht, einer Rechtleitung und einer Barmherzigkeit für mich.' }] },
        { id: 159, title: 'Bei Furcht', duas: [{ arabic: 'لَا إِلَهَ إِلَّا اللَّهُ', transliteration: 'La ilaha illallah.', translation: 'Es gibt keine Gottheit außer Allah.' }] },
        { id: 160, title: 'Was man sagt, wenn man etwas mag', duas: [{ arabic: 'مَا شَاءَ اللَّهُ، لَا قُوَّةَ إِلَّا بِاللَّهِ', transliteration: 'Ma sha` Allah, la quwwata illa billah.', translation: 'Was Allah will, (geschieht). Es gibt keine Macht außer bei Allah.' }] },
        { id: 161, 'title': 'Was man sagt, wenn man etwas nicht mag', 'duas': [{'arabic': 'قَدَرُ اللَّهِ وَمَا شَاءَ فَعَلَ', 'transliteration': 'Qadarul-lahi wa ma sha`a fa`al.', 'translation': 'Es ist das Schicksal Allahs, und was Er will, tut Er.' }] },
        { id: 162, 'title': 'Wenn man ein Haus betritt', 'duas': [{'arabic': 'بِسْمِ اللهِ وَلَجْنَا، وَبِسْمِ اللهِ خَرَجْنَا، وَعَلَى رَبِّنَا تَوَكَّلْنَا', 'transliteration': 'Bismillahi walajna, wa bismillahi kharajna, wa `ala rabbina tawakkalna.', 'translation': 'Im Namen Allahs treten wir ein, im Namen Allahs treten wir aus, und auf unseren Herrn vertrauen wir.' }] },
        { id: 163, 'title': 'Bei der Kondolenz', 'duas': [{'arabic': 'إِنَّ لِلَّهِ مَا أَخَذَ، وَلَهُ مَا أَعْطَى، وَكُلُّ شَيْءٍ عِنْدَهُ بِأَجَلٍ مُsَمًّى فَلْتَصْبِرْ وَلْتَحْتَسِبْ', 'transliteration': 'Inna lillahi ma akhadh, wa lahu ma a`ta, wa kullu shay`in `indahu bi-ajalin musamma... faltasbir waltahtasib.', 'translation': 'Wahrlich, Allah gehört, was Er genommen hat, und Ihm gehört, was Er gegeben hat, und alles bei Ihm hat eine festgesetzte Frist... So sei geduldig und hoffe auf Belohnung.' }] },
        { id: 164, 'title': 'Gebet für die Krankenheilung', 'duas': [{'arabic': 'اللَّهُمَّ رَبَّ النَّاسِ، أَذْهِبِ الْبَأْسَ، اشْفِهِ وَأَنْتَ الشَّافِي، لاَ شِفَاءَ إِلاَّ شِفَاؤُكَ، شِفَاءً لاَ يُغَادِرُ sَقَمًا', 'transliteration': 'Allahumma rabban-nas, adhhibil-ba`s, ishfihi wa antash-shafi, la shifa`a illa shifa`uk, shifa`an la yughadiru saqama.', 'translation': 'O Allah, Herr der Menschen, entferne das Leid und heile ihn, denn Du bist der Heiler. Es gibt keine Heilung außer Deiner Heilung, eine Heilung, die keine Krankheit zurücklässt.' }] },
        { id: 165, 'title': 'Wenn man Angst vor jemandem hat', 'duas': [{'arabic': 'اللَّهُمَّ اكْفِنِيهِمْ بِمَا شِئْتَ', 'transliteration': 'Allahumma-kfinihim bima shi`t.', 'translation': 'O Allah, genüge mir gegen sie, wie immer Du willst.' }] },
        { id: 166, 'title': 'Wenn man einen Muslim glücklich macht', 'duas': [{'arabic': 'أَحَبُّ الأَعْمَالِ إِلَى اللَّهِ sُرُورٌ تُدْخِلُهُ عَلَى مُsْلِمٍ', 'transliteration': 'Ahabbul-a`mali ilal-lahi sururun tudkhiluhu `ala muslim.', 'translation': 'Die beliebteste Tat bei Allah ist die Freude, die du einem Muslim bringst.' }] },
        { id: 167, 'title': 'Beim Anblick von Obst in der neuen Saison', 'duas': [{'arabic': 'اللَّهُمَّ بَارِكْ لَنَا فِي ثَمَرِنَا، وَبَارِكْ لَنَا فِي مَدِينَتِنَا، وَبَارِكْ لَنَا فِي صَاعِنَا، وَبَارِكْ لَنَا فِي مُدِّنَا', 'transliteration': 'Allahumma barik lana fi thamarina, wa barik lana fi madinatina, wa barik lana fi sa`ina, wa barik lana fi muddina.', 'translation': 'O Allah, segne uns in unseren Früchten, und segne uns in unserer Stadt, und segne uns in unserem Sa` und segne uns in unserem Mudd.' }] },
        { id: 168, 'title': 'Gebet gegen Faulheit', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنَ الْعَجْزِ وَالْكَsَلِ', 'transliteration': 'Allahumma inni a`udhu bika minal-`ajzi wal-kasal.', 'translation': 'O Allah, ich suche Zuflucht bei Dir vor Unfähigkeit und Faulheit.' }] },
        { id: 169, 'title': 'Für das Wohl der Muslime', 'duas': [{'arabic': 'اللَّهُمَّ أَصْلِحْ ذَاتَ بَيْنِنَا، وَأَلِّفْ بَيْنَ قُلُوبِنَا', 'transliteration': 'Allahumma aslih dhata baynina, wa allif bayna qulubina.', 'translation': 'O Allah, verbessere unsere Beziehungen und einige unsere Herzen.' }] },
        { id: 170, 'title': 'Beim Verlassen einer Versammlung', 'duas': [{'arabic': 'sُبْحَانَكَ اللَّهُمَّ وَبِحَمْدِكَ، أَشْهَدُ أَنْ لَا إِلَهَ إِلَّا أَنْتَ، أَsْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', 'transliteration': 'Subhanakallahumma wa bihamdik, ashhadu an la ilaha illa ant, astaghfiruka wa atubu ilayk.', 'translation': 'Gepriesen seist Du, o Allah, und mit Deinem Lob. Ich bezeuge, dass es keine Gottheit gibt außer Dir. Ich bitte Dich um Vergebung und bereue vor Dir.' }] },
        { id: 171, 'title': 'Opening of the Prayer', duas: [{ arabic: 'sُبْحَانَكَ اللَّهُمَّ وَبِحَمْدِكَ، وَتَبَارَكَ اسْمُكَ، وَتَعَالَى جَدُّكَ، وَلَا إِلَهَ غَيْرُكَ', transliteration: 'Subhanakallahumma wa bihamdik, wa tabarakasmuk, wa ta`ala jadduk, wa la ilaha ghayruk.', 'translation': 'Glory is to You, O Allah, and with Your praise. Blessed is Your name and exalted is Your majesty. And there is no deity other than You.' }] },
        { id: 172, 'title': 'During Ruku (bowing)', duas: [{ arabic: 'sُبْحَانَ رَبِّيَ الْعَظِيمِ', transliteration: 'Subhana Rabbiyal-`Azim.', 'translation': 'Glory to my Lord, the Almighty (3 times).' }] },
        { id: 173, 'title': 'When rising from Ruku', duas: [{ arabic: 'sَمِعَ اللَّهُ لِمَنْ حَمِدَهُ', transliteration: 'Sami`allahu liman hamidah.', 'translation': 'Allah hears him who praises Him.' }] },
        { id: 174, 'title': 'After rising from Ruku', duas: [{ arabic: 'رَبَّنَا وَلَكَ الْحَمْدُ', transliteration: 'Rabbana wa lakal-hamd.', 'translation': 'Our Lord, and to You be all praise.' }] },
        { id: 175, 'title': 'During Sujud (prostration)', duas: [{ arabic: 'sُبْحَانَ رَبِّيَ الْأَعْلَى', transliteration: 'Subhana Rabbiyal-A`la.', 'translation': 'Glory to my Lord, the Most High (3 times).' }] },
        { id: 176, 'title': 'Between the two prostrations', duas: [{ arabic: 'رَبِّ اغْفِرْ لِي، رَبِّ اغْfِرْ لِي', transliteration: 'Rabbigh-fir li, Rabbigh-fir li.', 'translation': 'My Lord, forgive me. My Lord, forgive me.' }] },
        { id: 177, 'title': 'During Tashahhud', duas: [{ arabic: 'التَّحِيَّاتُ لِلَّهِ وَالصَّلَوَاتُ وَالطَّيِّبَاتُ، السَّلَامُ عَلَيْكَ أَيُّهَا النَّبِيُّ وَرَحْمَةُ اللَّهِ وَبَرَكَاتُهُ، السَّلَامُ عَلَيْنَا وَعَلَى عِبَادِ اللَّهِ الصَّالِحِينَ، أَشْهَدُ أَنْ لَا إِلَهَ إِلَّا اللَّهُ وَأَشْهَدُ أَنَّ مُحَمَّدًا عَبْدُهُ وَرَسُولُهُ', transliteration: 'At-tahiyyatu lillahi was-salawatu wat-tayyibat, as-salamu `alayka ayyuhan-nabiyyu wa rahmatullahi wa barakatuh, as-salamu `alayna wa `ala `ibadillahis-salihin, ashhadu an la ilaha illallah, wa ashhadu anna Muhammadan `abduhu wa rasuluh.', 'translation': 'All greetings, prayers, and good things are for Allah. Peace be upon you, O Prophet, and the mercy of Allah and His blessings. Peace be upon us and upon the righteous servants of Allah. I bear witness that there is no deity but Allah, and I bear witness that Muhammad is His servant and His Messenger.' }] },
        { id: 178, 'title': 'After Tashahhud', duas: [{ arabic: 'اللَّهُمَّ صَلِّ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ كَمَا صَلَّيْتَ عَلَى إِبْرَاهِيمَ وَعَلَى آلِ إِبْرَاهِيمَ إِنَّكَ حَمِيدٌ مَجِيدٌ، اللَّهُمَّ بَارِكْ عَلَى مُحَمَّدٍ وَعَلَى آلِ مُحَمَّدٍ كَمَا بَارَكْتَ عَلَى إِبْرَاهِيمَ وَعَلَى آلِ إِبْرَاهِيمَ إِنَّكَ حَمِيدٌ مَجِيدٌ', transliteration: 'Allahumma salli `ala Muhammadin wa `ala aali Muhammad, kama sallayta `ala Ibrahima wa `ala aali Ibrahim, innaka Hamidun Majid. Allahumma barik `ala Muhammadin wa `ala aali Muhammad, kama barakta `ala Ibrahima wa `ala aali Ibrahim, innaka Hamidun Majid.', 'translation': 'O Allah, send Your grace on Muhammad and the family of Muhammad, as You sent Your grace on Ibrahim and the family of Ibrahim. Verily, You are the Praiseworthy, the Glorious. O Allah, bless Muhammad and the family of Muhammad, as You blessed Ibrahim and the family of Ibrahim. Verily, You are the Praiseworthy, the Glorious.' }] },
        { id: 179, 'title': 'Before Taslim', duas: [{ arabic: 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ عَذَابِ الْقَبْرِ، وَمِنْ عَذَابِ جَهَنَّمَ، وَمِنْ فِتْنَةِ الْمَحْيَا وَالْمَمَاتِ، وَمِنْ شَرِّ فِتْنَةِ الْمَسِيحِ الدَّجَّالِ', transliteration: 'Allahumma inni a`udhu bika min `adhabil-qabr, wa min `adhabi jahannam, wa min fitnatil-mahya wal-mamat, wa min sharri fitnatil-masihid-dajjal.', 'translation': 'O Allah, I seek refuge in You from the torment of the grave, from the torment of Hell, from the trials of life and death, and from the evil of the trial of the false Messiah (Dajjal).' }] },
        { id: 180, 'title': 'After Prayer', duas: [{ arabic: 'أَسْتَغْفِرُ اللَّهَ (ثَلَاثًا) اللَّهُمَّ أَنْتَ السَّلَامُ وَمِنْكَ السَّلَامُ، تَبَارَكْتَ يَا ذَا الْجَلَالِ وَالْإِكْرَامِ', transliteration: 'Astaghfirullah (3 times). Allahumma antas-salam wa minkas-salam, tabarakta ya dhal-jalali wal-ikram.', 'translation': 'I ask Allah for forgiveness (3 times). O Allah, You are Peace and from You comes peace. Blessed are You, O Owner of Majesty and Honor.' }] },
        { id: 181, 'title': 'Prayer for Steadfastness', 'duas': [{'arabic': 'يَا مُقَلِّبَ الْقُلُوبِ ثَبِّتْ قَلْبِي عَلَى دِينِكَ', 'transliteration': 'Ya muqallibal-qulub, thabbit qalbi `ala dinik.', 'translation': 'O Turner of the hearts, make my heart firm upon Your religion.'}]},
        { id: 182, 'title': 'When wishing good for someone', 'duas': [{'arabic': 'بَارَكَ اللَّهُ فِيكَ', 'transliteration': 'Barakallahu fik.', 'translation': 'May Allah bless you.'}]},
        { id: 183, 'title': 'To seek protection from evil', 'duas': [{'arabic': 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّاتِ مِنْ شَرِّ مَا خَلَقَ', 'transliteration': 'A`udhu bikalimatil-lahit-tammati min sharri ma khalaq.', 'translation': 'I seek refuge in the perfect words of Allah from the evil of what He has created.'}]},
        { id: 184, 'title': 'Upon meeting a Muslim', 'duas': [{'arabic': 'السَّلَامُ عَلَيْكُمْ وَرَحْمَةُ اللَّهِ وَبَرَكَاتُهُ', 'transliteration': 'As-salamu `alaykum wa rahmatullahi wa barakatuh.', 'translation': 'Peace be upon you and the mercy of Allah and His blessings.'}]},
        { id: 185, 'title': 'When receiving the greeting of peace', 'duas': [{'arabic': 'وَعَلَيْكُمُ السَّلَamُ وَرَحْمَةُ اللَّهِ وَبَرَكَاتُهُ', 'transliteration': 'Wa `alaykumus-salamu wa rahmatullahi wa barakatuh.', 'translation': 'And upon you be peace and the mercy of Allah and His blessings.'}]},
        { id: 186, 'title': 'Before sleeping', 'duas': [{'arabic': 'بِاسْمِكَ رَبِّي وَضَعْتُ جَنْبِي، وَبِكَ أَرْفَعُهُ، فَإِنْ أَمْسَكْتَ نَفْسِي فَارْحَمْهَا، وَإِنْ أَرْسَلْتَهَا فَاحْفَظْهَا بِمَا تَحْفَظُ بِهِ عِبَادَكَ الصَّالِحِينَ', 'transliteration': 'Bismika rabbi wada`tu janbi, wa bika arfa`uh, fa-in amsakta nafsi farhamha, wa in arsaltaha fahfazha bima tahfazu bihi `ibadakas-salihin.', 'translation': 'In Your name my Lord, I lie down, and in Your name I rise. If You take my soul, have mercy on it, and if You send it back, protect it as You protect Your righteous servants.'}]},
        { id: 187, 'title': 'When waking up at night with fear', 'duas': [{'arabic': 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّاتِ مِنْ غَضَبِهِ وَعِقَابِهِ، وَشَرِّ عِبَادِهِ، وَمِنْ هَمَزَاتِ الشَّيَاطِينِ وَأَنْ يَحْضُرُونِ', 'transliteration': 'A`udhu bikalimatil-lahit-tammati min ghadabihi wa `iqabih, wa sharri `ibadih, wa min hamazatish-shayatin wa an yahdurun.', 'translation': 'I seek refuge in the perfect words of Allah from His anger and His punishment, from the evil of His servants, and from the whispers of the devils and that they should come to me.'}]},
        { id: 188, 'title': 'To protect oneself from the evil of the tongue', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ شَرِّ سَمْعِي، وَمِنْ شَرِّ بَصَرِي، وَمِنْ شَرِّ لِسَانِي، وَمِنْ شَرِّ قَلْبِي، وَمِنْ شَرِّ مَنِيِّي', 'transliteration': 'Allahumma inni a`udhu bika min sharri sam`i, wa min sharri basari, wa min sharri lisani, wa min sharri qalbi, wa min sharri maniyyi.', 'translation': 'O Allah, I seek refuge in You from the evil of my hearing, the evil of my sight, the evil of my tongue, the evil of my heart, and the evil of my seed.'}]},
        { id: 189, 'title': 'Prayer for wisdom', 'duas': [{'arabic': 'اللَّهُمَّ آتِنَا فِي الدُّنْيَا حَسَنَةً وَفِي الْآخِرَةِ حَسَنَةً وَقِنَا عَذَابَ النَّارِ', 'transliteration': 'Allahumma atina fid-dunya hasanatan wa fil-akhirati hasanatan waqina `adhaban-nar.', 'translation': 'O Allah, give us good in this world and good in the Hereafter, and save us from the punishment of the Fire.'}]},
        { id: 190, 'title': 'Gratitude for Allah\'s blessings', 'duas': [{'arabic': 'اللَّهُمَّ مَا أَصْبَحَ بِي مِنْ نِعْمَةٍ أَوْ بِأَحَدٍ مِنْ خَلْقِكَ فَمِنْكَ وَحْدَكَ لَا شَرِيكَ لَكَ، فَلَكَ الْحَمْدُ وَلَكَ الشُّكْرُ', 'transliteration': 'Allahumma ma asbaha bi min ni`matin aw bi-ahadin min khalqik, fa-minka wahdaka la sharika lak, fa-lakal-hamdu wa lakash-shukr.', 'translation': 'O Allah, whatever blessing has come to me or to any of Your creation this morning, it is from You alone, You have no partner. So to You be all praise and all thanks.'}]},
        { id: 191, 'title': 'Protection from hypocrisy', 'duas': [{'arabic': 'اللَّهُمَّ طَهِّرْ قَلْبِي مِنَ النِّفَاقِ، وَعَمَلِي مِنَ الرِّيَاءِ، وَلِسَانِي مِنَ الْكَذِبِ، وَعَيْنِي مِنَ الْخِيَانَةِ', 'transliteration': 'Allahumma tahhir qalbi minan-nifaq, wa `amali minar-riya, wa lisani minal-kadhib, wa `ayni minal-khiyanah.', 'translation': 'O Allah, purify my heart from hypocrisy, my deeds from showing off, my tongue from lying, and my eyes from treachery.'}]},
        { id: 192, 'title': 'Prayer for a good end', 'duas': [{'arabic': 'اللَّهُمَّ اجْعَلْ خَيْرَ عُمْرِي آخِرَهُ، وَخَيْرَ عَمَلِي خَوَاتِمَهُ، وَخَيْرَ أَيَّامِي يَوْمَ أَلْقَاكَ فِيهِ', 'transliteration': 'Allahummaj-`al khayra `umri akhirah, wa khayra `amali khawatimahu, wa khayra ayyami yawma alqaka fih.', 'translation': 'O Allah, make the end of my life the best part of my life, my last deed my best deed, and my best day the day I meet You.'}]},
        { id: 193, 'title': 'When leaving the market', 'duas': [{'arabic': 'لَا إِلَهَ إِلَّا اللَّهُ وَحْدَهُ لَا شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، يُحْيِي وَيُمِيتُ وَهُوَ حَيٌّ لَا يَمُوتُ، بِيَدِهِ الْخَيْرُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'transliteration': 'La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, yuhyi wa yumit, wa huwa hayyun la yamut, bi-yadihil-khayr, wa huwa `ala kulli shay\'in qadir.', 'translation': 'There is no god but Allah alone, with no partner. His is the dominion and His is all praise. He gives life and causes death, and He is living and does not die. In His hand is all good, and He is over all things competent.'}]},
        { id: 194, 'title': 'To seek protection from the evil of creation', 'duas': [{'arabic': 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّاتِ مِنْ شَرِّ مَا خَلَقَ', 'transliteration': 'A`udhu bikalimatil-lahit-tammati min sharri ma khalaq.', 'translation': 'I seek refuge in the perfect words of Allah from the evil of what He has created.'}]},
        { id: 195, 'title': 'When one loses something', 'duas': [{'arabic': 'يَا جَامِعَ النَّاسِ لِيَوْمٍ لَا رَيْبَ فِيهِ، اجْمَعْ عَلَيَّ ضَالَّتِي', 'transliteration': 'Ya jami`an-nasi li-yawmin la rayba fih, ijma` `alayya dallati.', 'translation': 'O Gatherer of mankind for a Day about which there is no doubt, bring back to me my lost item.'}]},
        { id: 196, 'title': 'For healing the sick', 'duas': [{'arabic': 'أَسْأَلُ اللَّهَ الْعَظِيمَ رَبَّ الْعَرْشِ الْعَظِيمِ أَنْ يَشْفِيَكَ', 'transliteration': 'As`alullahal-`azim, rabbal-`arshil-`azim, an yashfiyak.', 'translation': 'I ask Allah, the Mighty, the Lord of the Mighty Throne, to cure you.'}]},
        { id: 197, 'title': 'Upon encountering difficulty', 'duas': [{'arabic': 'اللَّهُمَّ لَا سَهْلَ إِلَّا مَا جَعَلْتَهُ sَهْلًا وَأَنْتَ تَجْعَلُ الْحَزْنَ إِذَا شِئْتَ sَهْلًا', 'transliteration': 'Allahumma la sahla illa ma ja`altahu sahla, wa anta taj`alul-hazna idha shi`ta sahla.', 'translation': 'O Allah, there is no ease except in that which You have made easy, and You make the difficulty, if You wish, easy.'}]},
        { id: 198, 'title': 'When entering the house of a sick person', 'duas': [{'arabic': 'لَا بَأْسَ طَهُورٌ إِنْ شَاءَ اللَّهُ', 'transliteration': 'La ba`sa, tahurun in sha` Allah.', 'translation': 'Do not worry, it is a purification, if Allah wills.'}]},
        { id: 199, 'title': 'For someone in difficulty', 'duas': [{'arabic': 'إِنَّا لِلَّهِ وَإِنَّا إِلَيْهِ رَاجِعُونَ', 'transliteration': 'Inna lillahi wa inna ilayhi raji`un.', 'translation': 'Indeed, we belong to Allah, and to Him we are to return.'}]},
        { id: 200, 'title': 'To fulfill one\'s needs', 'duas': [{'arabic': 'حَسْبِيَ اللَّهُ لَا إِلَهَ إِلَّا هُوَ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيمِ', 'transliteration': 'Hasbiyallahu la ilaha illa huw, `alayhi tawakkaltu wa huwa rabbul-`arshil-`azim.', 'translation': 'Allah is sufficient for me. There is no deity but Him. In Him I trust, and He is the Lord of the Magnificent Throne.'}]},
        { id: 201, 'title': 'Protection from the evil of the eyes', 'duas': [{'arabic': 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّةِ مِنْ كُلِّ شَيْطَانٍ وَهَامَّةٍ، وَمِنْ كُلِّ عَيْنٍ لَامَّةٍ', 'transliteration': 'A`udhu bikalimatil-lahit-tammati min kulli shaytanin wa hammah, wa min kulli `aynin lammah.', 'translation': 'I seek refuge in the perfect words of Allah, from every devil and every poisonous reptile, and from every evil eye.'}]},
        { id: 202, 'title': 'To express gratitude', 'duas': [{'arabic': 'الْحَمْدُ لِلَّهِ حَمْدًا كَثِيرًا طَيِّبًا مُبَارَكًا فِيهِ', 'transliteration': 'Alhamdu lillahi hamdan kathiran tayyiban mubarakan fih.', 'translation': 'All praise is to Allah, much, good and blessed praise.'}]},
        { id: 203, 'title': 'When contemplating creation', 'duas': [{'arabic': 'رَبَّنَا مَا خَلَقْتَ هَذَا بَاطِلًا sُبْحَانَكَ فَقِنَا عَذَابَ النَّارِ', 'transliteration': 'Rabbana ma khalaqta hadha batila, subhanaka faqina `adhaban-nar.', 'translation': 'Our Lord, You have not created this without purpose. Glory to You! So protect us from the punishment of the Fire.'}]},
        { id: 204, 'title': 'To ask for forgiveness for parents', 'duas': [{'arabic': 'رَبِّ اغْفِرْ لِي وَلِوَالِدَيَّ', 'transliteration': 'Rabbigh-fir li wa li-walidayy.', 'translation': 'My Lord, forgive me and my parents.'}]},
        { id: 205, 'title': 'To increase knowledge', 'duas': [{'arabic': 'رَبِّ زِدْنِي عِلْمًا', 'transliteration': 'Rabbi zidni `ilma.', 'translation': 'My Lord, increase me in knowledge.'}]},
        { id: 206, 'title': 'Before an exam', 'duas': [{'arabic': 'رَبِّ اشْرَحْ لِي صَدْرِي وَيَسِّرْ لِي أَمْرِي وَاحْلُلْ عُقْدَةً مِنْ لِسَانِي يَفْقَهُوا قَوْلِي', 'transliteration': 'Rabbish-rah li sadri, wa yassir li amri, wahlul `uqdatan min lisani, yafqahu qawli.', 'translation': 'My Lord, expand for me my breast, and ease for me my task, and untie the knot from my tongue, that they may understand my speech.'}]},
        { id: 207, 'title': 'After an exam', 'duas': [{'arabic': 'الْحَمْدُ لِلَّهِ الَّذِي هَدَانَا لِهَذَا وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَا أَنْ هَدَانَا اللَّهُ', 'transliteration': 'Alhamdu lillahil-ladhi hadana lihaza wa ma kunna linahtadiya lawla an hadanallah.', 'translation': 'All praise is to Allah who has guided us to this. We would not have been guided had Allah not guided us.'}]},
        { id: 208, 'title': 'For a friend', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ حُبَّكَ وَحُبَّ مَنْ يُحِبُّكَ وَالْعَمَلَ الَّذِي يُبَلِّغُنِي حُبَّكَ', 'transliteration': 'Allahumma inni as`aluka hubbak, wa hubba man yuhibbuk, wal-`amalal-ladhi yuballighuni hubbak.', 'translation': 'O Allah, I ask You for Your love, the love of those who love You, and the deeds that will bring me to Your love.'}]},
        { id: 209, 'title': 'When embarking on a journey', 'duas': [{'arabic': 'أَسْتَوْدِعُكُمُ اللَّهَ الَّذِي لَا تَضِيعُ وَدَائِعُهُ', 'transliteration': 'Astawdi`ukumul-lahal-ladhi la tadi`u wada`i`uh.', 'translation': 'I entrust you to Allah whose trusts are not lost.'}]},
        { id: 210, 'title': 'For someone embarking on a journey', 'duas': [{'arabic': 'زَوَّدَكَ اللَّهُ التَّقْوَى، وَغَفَرَ ذَنْبَكَ، وَيَسَّرَ لَكَ الْخَيْرَ حَيْثُمَا كُنْتَ', 'transliteration': 'Zawwadakal-lahut-taqwa, wa ghafara dhanbak, wa yassara lakal-khayra haythuma kunt.', 'translation': 'May Allah provide you with Taqwa (piety), forgive your sins, and make goodness easy for you wherever you are.'}]},
        { id: 211, 'title': 'When entering a new place', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ خَيْرَهَا وَخَيْرَ أَهْلِهَا، وَأَعُوذُ بِكَ مِنْ شَرِّهَا وَشَرِّ أَهْلِهَا', 'transliteration': 'Allahumma inni as`aluka khayraha wa khayra ahliha, wa a`udhu bika min sharriha wa sharri ahliha.', 'translation': 'O Allah, I ask You for the good of this place and the good of its people, and I seek refuge in You from its evil and the evil of its people.'}]},
        { id: 212, 'title': 'When afraid of loneliness', 'duas': [{'arabic': 'اللَّهُمَّ آنِسْ وَحْشَتِي فِي قَبْرِي', 'transliteration': 'Allahumma anis wahshati fi qabri.', 'translation': 'O Allah, relieve my loneliness in my grave.'}]},
        { id: 213, 'title': 'To seek protection from Hell', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ عَذَابِ جَهَنَّمَ', 'transliteration': 'Allahumma inni a`udhu bika min `adhabi jahannam.', 'translation': 'O Allah, I seek refuge in You from the torment of Hell.'}]},
        { id: 214, 'title': 'To ask for Paradise', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ الْجَنَّةَ وَمَا قَرَّبَ إِلَيْهَا مِنْ قَوْلٍ أَوْ عَمَلٍ', 'transliteration': 'Allahumma inni as`alukal-jannata wa ma qarraba ilayha min qawlin aw `amal.', 'translation': 'O Allah, I ask You for Paradise and for that which brings me closer to it of speech or action.'}]},
        { id: 215, 'title': 'When performing a good deed', 'duas': [{'arabic': 'اللَّهُمَّ تَقَبَّلْ مِنَّا إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ', 'transliteration': 'Allahumma taqabbal minna, innaka antas-sami`ul-`alim.', 'translation': 'O Allah, accept from us. Verily, You are the All-Hearing, the All-Knowing.'}]},
        { id: 216, 'title': 'Beim Betrachten des Spiegels', 'duas': [{'arabic': 'اللَّهُمَّ كَمَا حَسَّنْتَ خَلْقِي فَحَسِّنْ خُلُقِي', 'transliteration': 'Allahumma kama hassanta khalqi fahassin khuluqi.', 'translation': 'O Allah, so wie Du meine Gestalt schön gemacht hast, so mache auch meinen Charakter schön.'}]},
        { id: 217, 'title': 'Was man bei einem Unglücksfall sagt', 'duas': [{'arabic': 'إِنَّا لِلّهِ وَإِنَّـا إِلَيْهِ رَاجِعونَ', 'transliteration': 'Inna lillahi wa inna ilayhi raji\'un.', 'translation': 'Wahrlich, wir gehören Allah und zu Ihm werden wir zurückkehren.'}]},
        { id: 218, 'title': 'Gebet um Festigung im Glauben', 'duas': [{'arabic': 'يَا مُقَلِّبَ الْقُلُوبِ ثَبِّتْ قَلْبِي عَلَى دِينِكَ', 'transliteration': 'Ya muqallib al-quloob, thabbit qalbi \'ala deenik.', 'translation': 'O Du, der die Herzen wendet, festige mein Herz auf Deiner Religion.'}]},
        { id: 219, 'title': 'Gebet für Wissen, das nützt', 'duas': [{'arabic': 'اللَّهُمَّ انْفَعْنِي بِمَا عَلَّمْتَنِي، وَعَلِّمْنِي مَا يَنْفَعُنِي، وَزِدْنِي عِلْمًا', 'transliteration': 'Allahumma infa\'ni bima \'allamtani, wa \'allimni ma yanfa\'uni, wa zidni \'ilma.', 'translation': 'O Allah, lass mich von dem profitieren, was Du mich gelehrt hast, lehre mich, was mir nützt, und mehre mein Wissen.'}]},
        { id: 220, 'title': 'Schutz vor dem Übel der Zunge', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ شَرِّ سَمْعِي، وَمِنْ شَرِّ بَصَرِي، وَمِنْ شَرِّ لِسَانِي، وَمِنْ شَرِّ قَلْبِي، وَمِنْ شَرِّ مَنِيِّي', 'transliteration': 'Allahumma inni a\'udhu bika min sharri sam\'i, wa min sharri basari, wa min sharri lisani, wa min sharri qalbi, wa min sharri maniyyi.', 'translation': 'O Allah, ich suche Zuflucht bei Dir vor dem Übel meines Gehörs, dem Übel meines Sehens, dem Übel meiner Zunge, dem Übel meines Herzens und dem Übel meiner Begierde.'}]},
        { id: 221, 'title': 'Schutz vor Heuchelei', 'duas': [{'arabic': 'اللَّهُمَّ طَهِّرْ قَلْبِي مِنَ النِّفَاقِ، وَعَمَلِي مِنَ الرِّيَاءِ، وَلِسَانِي مِنَ الْكَذِبِ، وَعَيْنِي مِنَ الْخِيَانَةِ', 'transliteration': 'Allahumma tahhir qalbi minan-nifaq, wa \'amali minar-riya\', wa lisani minal-kadhib, wa \'ayni minal-khiyanah.', 'translation': 'O Allah, reinige mein Herz von Heuchelei, meine Taten von Augendienerei, meine Zunge von Lüge und meine Augen von Verrat.'}]},
        { id: 222, 'title': 'Gebet um ein gutes Ende', 'duas': [{'arabic': 'اللَّهُمَّ اجْعَلْ خَيْرَ عُمْرِي آخِرَهُ، وَخَيْرَ عَمَلِي خَوَاتِمَهُ، وَخَيْرَ أَيَّامِي يَوْمَ أَلْقَاكَ فِيهِ', 'transliteration': 'Allahumma-j\'al khayra \'umri akhirahu, wa khayra \'amali khawatimahu, wa khayra ayyami yawma alqaka fih.', 'translation': 'O Allah, mache das Ende meines Lebens zum besten Teil meines Lebens, meine letzte Tat zu meiner besten Tat und meinen besten Tag zu dem Tag, an dem ich Dir begegne.'}]},
        { id: 223, 'title': 'Wenn man etwas verliert', 'duas': [{'arabic': 'يَا جَامِعَ النَّاسِ لِيَوْمٍ لَا رَيْبَ فِيهِ، اجْمَعْ عَلَيَّ ضَالَّتِي', 'transliteration': 'Ya jami\'an-nasi li-yawmin la rayba fih, ijma\' \'alayya dallati.', 'translation': 'O Sammler der Menschen für einen Tag, an dem kein Zweifel besteht, bringe mir mein Verlorenes zurück.'}]},
        { id: 224, 'title': 'Für einen Freund', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ حُبَّكَ وَحُبَّ مَنْ يُحِبُّكَ وَالْعَمَلَ الَّذِي يُبَلِّغُنِي حُبَّكَ', 'transliteration': 'Allahumma inni as\'aluka hubbak, wa hubba man yuhibbuk, wal-\'amalal-ladhi yuballighuni hubbak.', 'translation': 'O Allah, ich bitte Dich um Deine Liebe, die Liebe derer, die Dich lieben, und die Taten, die mich zu Deiner Liebe bringen werden.'}]},
        { id: 225, 'title': 'Wenn man sich auf eine Reise begibt', 'duas': [{'arabic': 'أَسْتَوْدِعُكُمُ اللَّهَ الَّذِي لَا تَضِيعُ وَدَائِعُهُ', 'transliteration': 'Astawdi\'ukumul-lahal-ladhi la tadi\'u wada\'i\'uh.', 'translation': 'Ich vertraue dich Allah an, dessen anvertraute Güter nicht verloren gehen.'}]},
        { id: 226, 'title': 'Für jemanden, der sich auf eine Reise begibt', 'duas': [{'arabic': 'زَوَّدَكَ اللَّهُ التَّقْوَى، وَغَفَرَ ذَنْبَكَ، وَيَسَّرَ لَكَ الْخَيْرَ حَيْثُمَا كُنْتَ', 'transliteration': 'Zawwadakal-lahut-taqwa, wa ghafara dhanbak, wa yassara lakal-khayra haythuma kunt.', 'translation': 'Möge Allah dich mit Taqwa (Gottesfurcht) versorgen, deine Sünden vergeben und dir das Gute erleichtern, wo immer du bist.'}]},
        { id: 227, 'title': 'Wenn man einen neuen Ort betritt', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ خَيْرَهَا وَخَيْرَ أَهْلِهَا، وَأَعُوذُ بِكَ مِنْ شَرِّهَا وَشَرِّ أَهْلِهَا', 'transliteration': 'Allahumma inni as\'aluka khayraha wa khayra ahliha, wa a\'udhu bika min sharriha wa sharri ahliha.', 'translation': 'O Allah, ich bitte Dich um das Gute dieses Ortes und das Gute seiner Bewohner, und ich suche Zuflucht bei Dir vor seinem Übel und dem Übel seiner Bewohner.'}]},
        { id: 228, 'title': 'Wenn man Angst vor Einsamkeit hat', 'duas': [{'arabic': 'اللَّهُمَّ آنِسْ وَحْشَتِي فِي قَبْرِي', 'transliteration': 'Allahumma anis wahshati fi qabri.', 'translation': 'O Allah, lindere meine Einsamkeit in meinem Grab.'}]},
        { id: 229, 'title': 'Wenn man eine gute Tat vollbringt', 'duas': [{'arabic': 'اللَّهُمَّ تَقَبَّلْ مِنَّا إِنَّكَ أَنْتَ السَّمِيعُ الْعَلِيمُ', 'transliteration': 'Allahumma taqabbal minna, innaka antas-sami`ul-`alim.', 'translation': 'O Allah, nimm von uns an. Wahrlich, Du bist der Allhörende, der Allwissende.'}]},
        { id: 230, 'title': 'Gebet des Istikhara (Bitte um Führung)', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْتَخِيرُكَ بِعِلْمِكَ، وَأَسْتَقْدِرُكَ بِقُdْرَتِكَ... (vollständiges Dua)', 'transliteration': 'Allahumma inni astakhiruka bi\'ilmik, wa astaqdiruka bi-qudratik...', 'translation': 'O Allah, ich bitte Dich um Führung durch Dein Wissen und um Kraft durch Deine Macht... (vollständiges Dua)'}]},
        { id: 231, 'title': 'Beim Erwachen am Morgen', 'duas': [{'arabic': 'أَصْبَحْنَا وَأَصْبَحَ الْمُلْكُ لِلَّهِ، وَالْحَمْدُ لِلَّهِ', 'transliteration': 'Asbahna wa asbahal-mulku lillah, wal-hamdu lillah.', 'translation': 'Wir sind in den Morgen eingetreten und das Königreich gehört Allah, und alles Lob gebührt Allah.'}]},
        { id: 232, 'title': 'Beim Eintreten in den Abend', 'duas': [{'arabic': 'أَمْسَيْنَا وَأَمْسَى الْمُلْكُ لِلَّهِ، وَالْحَمْدُ لِلَّهِ', 'transliteration': 'Amsayna wa amsal-mulku lillah, wal-hamdu lillah.', 'translation': 'Wir sind in den Abend eingetreten und das Königreich gehört Allah, und alles Lob gebührt Allah.'}]},
        { id: 233, 'title': 'Was man sagt, bevor man isst', 'duas': [{'arabic': 'اللَّهُمَّ بَارِكْ لَنَا فِيمَا رَزَقْتَنَا، وَقِنَا عَذَابَ النَّارِ، بِسْمِ اللهِ', 'transliteration': 'Allahumma barik lana fima razaqtana, waqina \'adhaban-nar, bismillah.', 'translation': 'O Allah, segne uns in dem, was Du uns gegeben hast, und schütze uns vor der Strafe des Feuers. Im Namen Allahs.'}]},
        { id: 234, 'title': 'Wenn man vergisst, "Bismillah" zu sagen', 'duas': [{'arabic': 'بِسْمِ اللهِ أَوَّلَهُ وَآخِرَهُ', 'transliteration': 'Bismillahi awwalahu wa akhirahu.', 'translation': 'Im Namen Allahs, am Anfang und am Ende.'}]},
        { id: 235, 'title': 'Dua für den Gastgeber', 'duas': [{'arabic': 'أَفْطَرَ عِنْدَكُمُ الصَّائِمُونَ، وَأَكَلَ طَعَامَكُمُ الْأَبْرَارُ، وَصَلَّتْ عَلَيْكُمُ الْمَلَائِكَةُ', 'transliteration': 'Aftara \'indakumus-sa\'imun, wa akala ta\'amakumul-abrar, wa sallat \'alaykumul-mala\'ikah.', 'translation': 'Mögen die Fastenden bei euch ihr Fasten brechen, mögen die Rechtschaffenen eure Speise essen und mögen die Engel für euch beten.'}]},
        { id: 236, 'title': 'Beim Anblick einer muslimischen Person, die lächelt', 'duas': [{'arabic': 'أَضْحَكَ اللَّهُ سِنَّكَ', 'transliteration': 'Adhakallahu sinnak.', 'translation': 'Möge Allah dich zum Lachen bringen.'}]},
        { id: 237, 'title': 'Gebet für Standhaftigkeit bei Prüfungen', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ الثَّبَاتَ فِي الْأَمْرِ، وَالْعَزِيمَةَ عَلَى الرُّشْدِ', 'transliteration': 'Allahumma inni as\'alukath-thabata fil-amr, wal-\'azimata \'alar-rushd.', 'translation': 'O Allah, ich bitte Dich um Standhaftigkeit in der Angelegenheit und um Entschlossenheit auf dem rechten Weg.'}]},
        { id: 238, 'title': 'Gebet um Schutz vor Unglauben und Armut', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنَ الْكُفْرِ وَالْفَقْرِ', 'transliteration': 'Allahumma inni a\'udhu bika minal-kufri wal-faqr.', 'translation': 'O Allah, ich suche Zuflucht bei Dir vor dem Unglauben und der Armut.'}]},
        { id: 239, 'title': 'Gebet für das Gedenken, den Dank und die gute Anbetung', 'duas': [{'arabic': 'اللَّهُمَّ أَعِنِّي عَلَى ذِكْرِكَ وَشُكْرِكَ وَحُsْنِ عِبَادَتِكَ', 'transliteration': 'Allahumma a\'inni \'ala dhikrika wa shukrika wa husni \'ibadatik.', 'translation': 'O Allah, hilf mir, Deiner zu gedenken, Dir zu danken und Dich auf die beste Weise anzubeten.'}]},
        { id: 240, 'title': 'Um Schutz vor dem Bösen zu suchen, das man getan hat', 'duas': [{'arabic': 'أَبُوءُ لَكَ بِنِعْمَتِكَ عَلَيَّ، وَأَبُوءُ لَكَ بِذَنْبِي فَاغْفِرْ لِي، فَإِنَّهُ لَا يَغْفِرُ الذُّنُوبَ إِلَّا أَنْتَ', 'transliteration': 'Abu\'u laka bini\'matika \'alayya, wa abu\'u laka bidhanbi faghfir li, fa-innahu la yaghfirudh-dhunuba illa ant.', 'translation': 'Ich erkenne Deine Gaben an mir an und ich gestehe meine Sünde. So vergib mir, denn niemand vergibt Sünden außer Dir.'}]},
        { id: 241, title: 'Bei der Beerdigung eines Kindes', duas: [{ arabic: 'اللَّهُمَّ اجْعَلْهُ لَنَا فَرَطًا وَsَلَفًا وَأَجْرًا', transliteration: 'Allahumma-j’alhu lana faratan wa salafan wa ajra.', translation: 'O Allah, mache ihn zu unserem Vorläufer, einem Vorgänger und einer Belohnung für uns.' }] },
        { id: 242, title: 'Beim Besuch der Kranken', duas: [{ arabic: 'لاَ بَأْسَ طَهُورٌ إِنْ شَاءَ اللَّهُ', transliteration: 'La ba’sa tahurun in sha’ Allah.', translation: 'Kein Schaden, eine Reinigung, so Allah will.' }] },
        { id: 243, title: 'Beim Hören guter Nachrichten', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ الَّذِي بِنِعْمَتِهِ تَتِمُّ الصَّالِحَاتُ', transliteration: 'Alhamdulillahi-lladhi bini’matihi tatimmus-salihat.', translation: 'Alles Lob gebührt Allah, durch dessen Gnade gute Taten vollendet werden.' }] },
        { id: 244, title: 'Beim Hören schlechter Nachrichten', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ عَلَى كُلِّ حَالٍ', transliteration: 'Alhamdulillah \'ala kulli hal.', translation: 'Alles Lob gebührt Allah unter allen Umständen.' }] },
        { id: 245, title: 'Um Schutz vor Neid zu suchen', duas: [{ arabic: 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّاتِ مِنْ كُلِّ شَيْطَانٍ وَهَامَّةٍ، وَمِنْ كُلِّ عَيْنٍ لاَمَّةٍ', transliteration: 'A\'udhu bikalimatil-lahit-tammati min kulli shaytanin wa hammah, wa min kulli \'aynin lammah.', translation: 'Ich suche Zuflucht bei den vollkommenen Worten Allahs vor jedem Satan und jedem giftigen Tier und vor jedem bösen Blick.' }] },
        { id: 246, title: 'Wenn man Angst vor Shirk hat', duas: [{ arabic: 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ أَنْ أُشْرِكَ بِكَ وَأَنَا أَعْلَمُ، وَأَسْتَغْفِرُكَ لِمَا لاَ أَعْلَمُ', transliteration: 'Allahumma inni a\'udhu bika an ushrika bika wa ana a\'lam, wa astaghfiruka lima la a\'lam.', translation: 'O Allah, ich suche Zuflucht bei Dir davor, Dir wissentlich etwas beizugesellen, und ich bitte Dich um Vergebung für das, was ich nicht weiß.' }] },
        { id: 247, title: 'Um Vergebung für die muslimische Ummah zu bitten', duas: [{ arabic: 'اللَّهُمَّ اغْفِرْ لِلْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ، وَالْمُsْلِمِينَ وَالْمُsْلِمَاتِ، الْأَحْيَاءِ مِنْهُمْ وَالْأَمْوَاتِ', transliteration: 'Allahumma-ghfir lil-mu\'minina wal-mu\'minat, wal-muslimina wal-muslimat, al-ahya\'i minhum wal-amwat.', translation: 'O Allah, vergib den gläubigen Männern und Frauen, den muslimischen Männern und Frauen, den Lebenden von ihnen und den Toten.' }] },
        { id: 248, title: 'Wenn man in den Spiegel schaut', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ، اللَّهُمَّ كَمَا حَسَّنْتَ خَلْقِي فَحَسِّنْ خُلُقِي', transliteration: 'Alhamdulillah, Allahumma kama hassanta khalqi fahassin khuluqi.', translation: 'Alles Lob gebührt Allah. O Allah, so wie Du meine Gestalt schön gemacht hast, so mache auch meinen Charakter schön.' }] },
        { id: 249, title: 'Um Schutz vor dem Übel der Zunge zu suchen', duas: [{ arabic: 'اللَّهُمَّ إِنِّي أَعُوذُ بِكَ مِنْ شَرِّ لِسَانِي', transliteration: 'Allahumma inni a\'udhu bika min sharri lisani.', translation: 'O Allah, ich suche Zuflucht bei Dir vor dem Übel meiner Zunge.' }] },
        { id: 250, title: 'Gebet für jemanden, der einem Kleidung geschenkt hat', duas: [{ arabic: 'ابْلِ وَأَخْلِقْ، ثُمَّ ابْلِ وَأَخْلِقْ، ثُمَّ ابْلِ وَأَخْلِقْ', transliteration: 'Ibli wa akhliq, thumma ibli wa akhliq, thumma ibli wa akhliq.', translation: 'Trage es ab und erschaffe (Neues), dann trage es ab und erschaffe (Neues), dann trage es ab und erschaffe (Neues).' }] },
        { id: 251, title: 'Gebet für den, der einem Essen oder Trinken gibt', duas: [{ arabic: 'اللَّهُمَّ أَطْعِمْ مَنْ أَطْعَمَنِي، وَاسْقِ مَنْ sَقَانِي', transliteration: 'Allahumma at\'im man at\'amani, wasqi man saqani.', translation: 'O Allah, speise den, der mich gespeist hat, und gib dem zu trinken, der mir zu trinken gegeben hat.' }] },
        { id: 252, title: 'Beim Brechen des Fastens im Haus eines anderen', duas: [{ arabic: 'أَفْطَرَ عِنْدَكُمُ الصَّائِمُونَ، وَأَكَلَ طَعَامَكُمُ الْأَبْرَارُ، وَنَزَلَتْ عَلَيْكُمُ السَّكِينَةُ', transliteration: 'Aftara \'indakumus-sa\'imun, wa akala ta\'amakumul-abrar, wa nazalat \'alaykumus-sakinah.', translation: 'Mögen die Fastenden bei euch ihr Fasten brechen, mögen die Rechtschaffenen eure Speise essen und möge die innere Ruhe auf euch herabkommen.' }] },
        { id: 253, title: 'Gebet beim Anblick der ersten Früchte der Saison', duas: [{ arabic: 'اللَّهُمَّ بَارِكْ لَنَا فِي ثَمَرِنَا، وَبَارِكْ لَنَا فِي مَدِينَتِنَا، وَبَارِكْ لَنَا فِي صَاعِنَا وَفِي مُدِّنَا', transliteration: 'Allahumma barik lana fi thamarina, wa barik lana fi madinatina, wa barik lana fi sa\'ina wa fi muddina.', translation: 'O Allah, segne uns in unseren Früchten, segne uns in unserer Stadt, segne uns in unserem Sa\' und in unserem Mudd.' }] },
        { id: 254, title: 'Gebet bei Furcht vor einem Tyrannen', duas: [{ arabic: 'اللَّهُ أَكْبَرُ، اللَّهُ أَعَزُّ مِنْ خَلْقِهِ جَمِيعًا، اللَّهُ أَعَزُّ مِمَّا أَخَافُ وَأَحْذَرُ', transliteration: 'Allahu Akbar, Allahu a\'azzu min khalqihi jami\'a, Allahu a\'azzu mimma akhafu wa ahdhar.', translation: 'Allah ist der Größte, Allah ist mächtiger als alle Seine Geschöpfe. Allah ist mächtiger als das, was ich fürchte und wovor ich mich in Acht nehme.' }] },
        { id: 255, title: 'Wenn man sein Tier bestiegen hat', duas: [{ arabic: 'الْحَمْدُ لِلَّهِ. سُبْحَانَ الَّذِي sَخَّرَ لَنَا هَذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ وَإِنَّا إِلَى رَبِّنَا لَمُنْقَلِبُونَ', transliteration: 'Alhamdulillah. Subhanal-ladhi sakh-khara lana hadha wa ma kunna lahu muqrinin. Wa inna ila Rabbina lamunqalibun.', translation: 'Alles Lob gebührt Allah. Gepriesen sei Der, Der uns dies dienstbar gemacht hat, wozu wir selbst nicht fähig gewesen wären. Und wahrlich, zu unserem Herrn werden wir zurückkehren.' }] },
        { id: 256, title: 'Gebet um Schutz für die Familie während der Reise', duas: [{ arabic: 'أَسْتَوْدِعُكُمُ اللَّهَ الَّذِي لاَ تَضِيعُ وَدَائِعُهُ', transliteration: 'Astawdi\'ukumul-laha-lladhi la tadi\'u wada\'i\'uh.', translation: 'Ich vertraue euch Allah an, Dessen anvertraute Güter nicht verloren gehen.' }] },
        { id: 257, title: 'Gebet des Bewohners für den Reisenden', duas: [{ arabic: 'أَسْتَوْدِعُ اللَّهَ دِينَكَ، وَأَمَانَتَكَ، وَخَوَاتِيمَ عَمَلِكَ', transliteration: 'Astawdi\'ul-laha dinaka, wa amanataka, wa khawatima \'amalik.', translation: 'Ich vertraue Allah deine Religion, deine Vertrauenswürdigkeit und den Abschluss deiner Taten an.' }] },
        { id: 258, title: 'Gebet des Reisenden für die Bewohner', duas: [{ arabic: 'أَسْتَوْدِعُكُمُ اللَّهَ الَّذِي لاَ تَضِيعُ وَدَائِعُهُ', transliteration: 'Astawdi\'ukumul-laha-lladhi la tadi\'u wada\'i\'uh.', translation: 'Ich vertraue euch Allah an, Dessen anvertraute Güter nicht verloren gehen.' }] },
        { id: 259, title: 'Lobpreisung Allahs beim Auf- und Absteigen', duas: [{ arabic: 'كَانَ النَّبِيُّ صَلَّى اللَّهُ عَلَيْهِ وَsَلَّمَ وَأَصْحَابُهُ إِذَا عَلَوُا الثَّنَايَا كَبَّرُوا، وَإِذَا هَبَطُوا sَبَّحُوا', transliteration: 'Der Prophet ﷺ und seine Gefährten sagten "Allahu Akbar", wenn sie aufstiegen, und "Subhanallah", wenn sie abstiegen.', translation: 'The Prophet ﷺ and his companions would say "Allahu Akbar" when ascending a height and "Subhanallah" when descending.' }] },
        { id: 260, 'title': 'Gebet des Reisenden bei Einbruch der Dämmerung', 'duas': [{'arabic': 'أَمْسَى وَأَمْسَى الْمُلْكُ لِلَّهِ رَبِّ الْعَالَمِينَ...', 'transliteration': 'Amsa wa amsal-mulku lillahi rabbil-\'alamin...', 'translation': 'Wir haben den Abend erreicht und das Königreich gehört Allah, dem Herrn der Welten...' }] },
        { id: 261, 'title': 'Beim Absteigen an einem Ort', 'duas': [{'arabic': 'أَعُوذُ بِكَلِمَاتِ اللَّهِ التَّامَّاتِ مِنْ شَرِّ مَا خَلَقَ', 'transliteration': 'A\'udhu bikalimatil-lahit-tammati min sharri ma khalaq.', 'translation': 'Ich suche Zuflucht bei den vollkommenen Worten Allahs vor dem Übel dessen, was Er erschaffen hat.' }] },
        { id: 262, 'title': 'Beim Betreten des Marktes', 'duas': [{'arabic': 'لاَ إِلَهَ إِلاَّ اللَّهُ وَحْدَهُ لاَ شَرِيكَ لَهُ، لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ، يُحْيِي وَيُمِيتُ وَهُوَ حَيٌّ لاَ يَمُوتُ، بِيَدِهِ الْخَيْرُ وَهُوَ عَلَى كُلِّ شَيْءٍ قَدِيرٌ', 'transliteration': 'La ilaha illallahu wahdahu la sharika lah, lahul-mulku wa lahul-hamd, yuhyi wa yumit, wa huwa hayyun la yamut, bi-yadihil-khayr, wa huwa \'ala kulli shay\'in qadir.', 'translation': 'Es gibt keine Gottheit außer Allah allein, ohne Partner. Ihm gehört die Herrschaft und Ihm gehört alles Lob. Er gibt Leben und lässt sterben, und Er ist lebendig und stirbt nicht. In Seiner Hand ist alles Gute, und Er hat Macht über alle Dinge.' }] },
        { id: 263, 'title': 'Gebet gegen einen schlechten Vorboten', 'duas': [{'arabic': 'اللَّهُمَّ لاَ طَيْرَ إِلاَّ طَيْرُكَ، وَلاَ خَيْرَ إِلاَّ خَيْرُكَ، وَلاَ إِلَهَ غَيْرُكَ', 'transliteration': 'Allahumma la tayra illa tayruk, wa la khayra illa khayruk, wa la ilaha ghayruk.', 'translation': 'O Allah, es gibt kein Omen außer Deinem Omen, kein Gutes außer Deinem Guten und keine Gottheit außer Dir.' }] },
        { id: 264, 'title': 'Gebet für ein neu verheiratetes Paar', 'duas': [{'arabic': 'بَارَكَ اللَّهُ لَكَ، وَبَارَكَ عَلَيْكَ، وَجَمَعَ بَيْنَكُمَا فِي خَيْرٍ', 'transliteration': 'Barakallahu lak, wa baraka \'alayk, wa jama\'a baynakuma fi khayr.', 'translation': 'Möge Allah dich segnen, Seinen Segen über dich ausgießen und euch beide im Guten vereinen.' }] },
        { id: 265, 'title': 'Gebet des Ehemanns für seine Frau in der Hochzeitsnacht', 'duas': [{'arabic': 'اللَّهُمَّ إِنِّي أَsْأَلُكَ خَيْرَهَا وَخَيْرَ مَا جَبَلْتَهَا عَلَيْهِ، وَأَعُوذُ بِكَ مِنْ شَرِّهَا وَشَرِّ مَا جَبَلْتَهَا عَلَيْهِ', 'transliteration': 'Allahumma inni as\'aluka khayraha wa khayra ma jabaltaha \'alayh, wa a\'udhu bika min sharriha wa sharri ma jabaltaha \'alayh.', 'translation': 'O Allah, ich bitte Dich um ihr Gutes und das Gute, zu dem Du sie veranlagt hast, und ich suche Zuflucht bei Dir vor ihrem Schlechten und dem Schlechten, zu dem Du sie veranlagt hast.' }] },
        { id: 266, 'title': 'Gebet vor dem ehelichen Verkehr', 'duas': [{'arabic': 'بِسْمِ اللَّهِ، اللَّهُمَّ جَنِّبْنَا الشَّيْطَانَ، وَجَنِّبِ الشَّيْطَانَ مَا رَزَقْتَنَا', 'transliteration': 'Bismillah, Allahumma jannibnash-shaytan, wa jannibish-shaytana ma razaqtana.', 'translation': 'Im Namen Allahs. O Allah, halte den Satan von uns fern und halte den Satan fern von dem, was Du uns schenkst.' }] },
        { id: 267, 'title': 'Gebet gegen Zorn', 'duas': [{'arabic': 'أَعُوذُ بِاللَّهِ مِنَ الشَّيْطَانِ الرَّجِيمِ', 'transliteration': 'A\'udhu billahi minash-shaytanir-rajim.', 'translation': 'Ich suche Zuflucht bei Allah vor dem verfluchten Satan.' }] },
        { id: 268, 'title': 'Was man sagt, wenn man jemanden in Bedrängnis sieht', 'duas': [{'arabic': 'الْحَمْدُ لِلَّهِ الَّذِي عَافَانِي مِمَّا ابْتَلاَكَ بِهِ، وَفَضَّلَنِي عَلَى كَثِيرٍ مِمَّنْ خَلَقَ تَفْضِيلاً', 'transliteration': 'Alhamdulilla-hilladhi \'afani mimmab-talaka bih, wa faddalani \'ala kathirin mimman khalaqa tafdila.', 'translation': 'Alles Lob gebührt Allah, der mich vor dem bewahrt hat, womit Er dich geprüft hat, und mich vor vielen Seiner Geschöpfe bevorzugt hat.' }] },
        { id: 269, 'title': 'Sühne für eine Sitzung', 'duas': [{'arabic': 'sُبْحَانَكَ اللَّهُمَّ وَبِحَمْدِكَ، أَشْهَدُ أَنْ لاَ إِلَهَ إِلاَّ أَنْتَ، أَsْتَغْفِرُكَ وَأَتُوبُ إِلَيْكَ', 'transliteration': 'Subhanakallahumma wa bihamdik, ashhadu an la ilaha illa ant, astaghfiruka wa atubu ilayk.', 'translation': 'Gepriesen seist Du, o Allah, und mit Deinem Lob. Ich bezeuge, dass es keine Gottheit gibt außer Dir. Ich bitte Dich um Vergebung und bereue vor Dir.' }] },
        { id: 270, 'title': 'Gebet für den, der sagt: Möge Allah dir vergeben', 'duas': [{'arabic': 'وَلَكَ', 'transliteration': 'Wa lak.', 'translation': 'Und dir auch.' }] },
    ]
  }
}

export default function HisnulMuslimPage() {
    const { language } = useLanguage();
    const c = hisnulMuslimData[language] || hisnulMuslimData.de;
    const { toast } = useToast();

    const [user] = useAuthState(auth);
    const [searchTerm, setSearchTerm] = useState('');
    const [filteredChapters, setFilteredChapters] = useState(c.chapters);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        const results = c.chapters.filter(chapter => {
            const searchTermLower = searchTerm.toLowerCase();
            const titleMatch = chapter.title.toLowerCase().includes(searchTermLower);
            const duaMatch = chapter.duas.some(dua => 
                dua.arabic.toLowerCase().includes(searchTermLower) ||
                dua.transliteration.toLowerCase().includes(searchTermLower) ||
                dua.translation.toLowerCase().includes(searchTermLower)
            );
            return titleMatch || duaMatch;
        });
        setFilteredChapters(results);
    }, [searchTerm, c.chapters]);

    const handleSaveFavorite = async (textToSave: string) => {
        if (!user) {
            toast({
                title: c.loginToSave,
                variant: 'destructive',
                description: (
                    <Button variant="secondary" size="sm" asChild className="mt-2">
                        <Link href="/login">Login</Link>
                    </Button>
                ),
            });
            return;
        }

        setIsLoading(true);
        try {
            await addFavorite(user.uid, textToSave);
            toast({
                title: c.toastFavoriteSaved,
                description: (
                     <Button variant="link" asChild className="p-0">
                        <Link href="/favorites">View Favorites</Link>
                    </Button>
                )
            });
        } catch (error) {
            console.error("Error saving favorite:", error);
            toast({
                title: c.toastErrorSaving,
                variant: 'destructive',
            });
        } finally {
            setIsLoading(false);
        }
    }


  return (
    <div className="container mx-auto px-4 py-8">
      <Button asChild variant="ghost" className="mb-8">
        <Link href="/">
            <ArrowLeft className="mr-2 h-4 w-4" />
            {c.backToFeatures}
        </Link>
      </Button>
      <header className="text-center mb-12">
        <h1 className="text-4xl font-bold tracking-tight text-primary">
            {c.title}
        </h1>
        <p className="text-muted-foreground mt-2 text-lg max-w-2xl mx-auto">{c.description}</p>
      </header>
       <div className="max-w-3xl mx-auto mb-8">
            <Input 
                type="search"
                placeholder={c.searchPlaceholder}
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
                className="w-full"
            />
        </div>
      <div className="max-w-3xl mx-auto">
         <Accordion type="single" collapsible className="w-full">
              {filteredChapters.map((chapter) => (
                <AccordionItem key={chapter.id} value={`item-${chapter.id}`}>
                  <AccordionTrigger className="text-lg text-left hover:no-underline">
                     <div className="flex items-center text-left">
                        <span className="text-primary font-bold w-12 flex-shrink-0 text-right pr-4">{chapter.id}.</span> 
                        <span className="flex-1">{chapter.title}</span>
                     </div>
                  </AccordionTrigger>
                  <AccordionContent className="px-2 space-y-4">
                    {chapter.duas.map((dua, duaIndex) => (
                         <Card key={duaIndex} className="bg-muted/50">
                            <CardContent className="p-4 space-y-3">
                                <p className="text-xl font-quranic text-right tracking-wide leading-relaxed">{dua.arabic}</p>
                                <p className="text-muted-foreground italic text-sm">{dua.transliteration}</p>
                                <p className="text-foreground/90">"{dua.translation}"</p>
                                <div className="flex justify-end gap-2 pt-2 border-t">
                                     <Button variant="ghost" size="sm" asChild>
                                        <Link href={{
                                            pathname: '/memorization',
                                            query: { text: `${dua.arabic}\n\n${dua.translation}` }
                                        }}>
                                            <BrainCircuit className="mr-2 h-4 w-4" />
                                            {c.memorize}
                                        </Link>
                                    </Button>
                                    <Button variant="ghost" size="sm" onClick={() => handleSaveFavorite(`${chapter.title}\n\n${dua.arabic}\n\n${dua.translation}`)} disabled={isLoading}>
                                       {isLoading ? <Loader2 className="mr-2 h-4 w-4 animate-spin"/> : <Star className="mr-2 h-4 w-4" />}
                                       Favorit
                                    </Button>
                                </div>
                            </CardContent>
                        </Card>
                    ))}
                  </AccordionContent>
                </AccordionItem>
              ))}
            </Accordion>
      </div>
    </div>
  );
}
